/* Adjust margins */
#main-window[tabsontop="true"][sizemode="normal"] #TabsToolbar {    
    margin-left: 27px !important; 
    margin-right: 104px !important;
    margin-top:-5px !important;}

#toolbar-menubar { 
    background: none !important;
    height:26px ;
    margin-top: -3px;
    margin-bottom: 1px ;}

#main-window[tabsontop="true"][sizemode="normal"] #toolbar-menubar { 
    margin-left: 28px !important; 
    margin-top:-13px !important;
    margin-bottom: 4px ;}

#main-window[tabsontop="true"][sizemode="normal"] #appmenu-button-container {
    position: relative !important;
    z-index: 2 !important;}	
	
#main-window[tabsontop="true"][sizemode="maximized"] #TabsToolbar {    
    margin-left: -3px !important; 
    margin-right: -14px !important;}

/*Glass, slim and naked UI*/
#navigator-toolbox::after {
    height: 0 !important;}

#nav-bar {
    background-image: -moz-linear-gradient(rgba(255,255,255,.55), rgba(255,255,255,.3)) !important;
    background-color: transparent !important;
    border: none !important;
    margin-top: -1px !important;
    padding-top: 0px !important;
    height: 21px !important;
    visibility: visible !important;}
	
/*Hide the statusbar*/
#statusbar-display, statuspanel { 
    display: none !important; }

/*Change the throbber*/
.tabbrowser-tab .progress-bar { 
    display: none !important; }

.tab-throbber[busy], .tab-throbber[progress] { 
    list-style-image: url("data:image/png;base64,")  !important; }

/*Hide the Search Engine image*/
.searchbar-engine-image {
    display: none !important;}

/*Hide the search button (magnifying glass)*/
.search-go-button {
    display: none !important;}
    
/*Hide bookmark icons in the Personal Toolbar*/
toolbarbutton.bookmark-item > .toolbarbutton-icon {	
    display: none !important;}

/*Hide urlbar history dropmarker*/
.urlbar-history-dropmarker {
    display: none !important;}
    
/*Hide dropmarkers*/
/*#nav-bar toolbarbutton dropmarker {
    display: none !important;}*/

/*Disable tabstoolbar drag - enable newtab double clicking*/
#navigator-toolbox > #TabsToolbar, #main-window[tabsintitlebar] #TabsToolbar {
    -moz-binding: url("chrome://global/content/bindings/toolbar.xml#toolbar") !important;}
	
#urlbar-go-button, #urlbar-go-button:hover, #urlbar-reload-button, #urlbar-reload-button:hover, #urlbar-stop-button, #urlbar-stop-button:hover {
    background-image: none !important;
    box-shadow: none !important;}
	
#urlbar {
    background: transparent !important;
    color: #e0e0e0 !important; 
    font-weight:bold!important;
    border: none !important;
    border-radius: 2px 0 0 0 !important;
    margin: 0 !important;
    box-shadow: none !important;}

#urlbar:hover {
    background: transparent !important;
    color: #fff !important;
    text-shadow: #000000 1px 1px 0px !important;}

#page-proxy-stack {
    background: none !important;}
	
#identity-box:not(.verifiedIdentity):not(.verifiedDomain) {
    background: none !important;
    border: none !important;
    box-shadow: none !important;}
	
#identity-box.verifiedDomain, #identity-box.verifiedIdentity {
    border-radius: 0 !important;
    box-shadow: none !important;}

#searchbar > * {
    background: transparent !important;
    color: #e0e0e0 !important;
    font-weight:bold!important;
    border: none !important;
    border-radius: 2px 0 0 0 !important;
    margin: 0 !important;
    box-shadow: none !important;
    /*height: 21px !important; */}
	
#searchbar:hover > *, .findbar-textbox:hover {
    color: #fff !important;
    text-shadow: #000000 1px 1px 0px !important;}

#editBookmarkPanel {
    margin-right: -24px !important;}

#personal-bookmarks{
    background-color: transparent!important; } 

#PersonalToolbar{
    background-color: transparent!important;
    border: none !important;
    border-top: none !important;}

/* Defined min and max size of the tabs, default is 150-180px, change if desire below: */
.tabbrowser-tab:not([pinned])[fadein] {
    min-width: 150px !important;
    max-width: 180px !important;
    height:26px ;
    margin-left:1px !important;  }

.tabbrowser-tab:hover:not([pinned]){
    margin-left:1px !important;}
        
.tabbrowser-tab[selected]:not([pinned])[fadein] {
    min-width: 150px !important;
    max-width: 180px !important;
    margin-left:1px !important; margin-right:1px !important;}

/*Transparent Firefox Button and no text*/
#appmenu-button {
    background: none !important;
    border: none !important;
    box-shadow: none !important;}
	
#appmenu-button .button-text, #appmenu-button > .button-box > .button-menu-dropmarker {
    visibility: collapse !important;}

/* Adds a small, colored Firefox-button (expands on hover) */
#appmenu-button {
    width:22px;
    background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAHCAYAAADam2dgAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAABuSURBVChTY2BgYOABYkk8WAAox8B3/+eLybgwUF4IpEiUm5vbFaTowa+X/2EYxAfKOQOxBEgRExCrCQoKRsIUQhWEAsWVoPIgdQzMQKwhKSkZDVIA0gDSCBUHK4ABkEIVILaH0iA+VgCS4EQ3AQBMbTv9R0t4OwAAAABJRU5ErkJggg==)  center 25%  no-repeat !important;
    -moz-transition: background 0.5s !important;}

#appmenu-button:hover {
    width:28px !important;
    background:url(data:image/png;base64,) right 85%  no-repeat !important;
    -moz-transition: background .3s ease!important;}

#main-window[sizemode="maximized"][tabsontop="true"] #appmenu-button {
    margin: 0px 5px 5px 0px !important;
    padding: 0px 0px 0px 0px !important;
    min-width: 0px !important;
    height: 26px !important;
    border-radius: 0px 0px 0px 0px!important;}

#main-window[sizemode="normal"][tabsontop="true"] #appmenu-button {
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    min-width: 0px !important;
    min-height: 26px !important;
    border-radius: 0px 0px 0px 0px!important;}

/* Changes on the tabs */
.tabs-newtab-button {
    min-width:22px !important;
    width:22px !important;
    margin-left:2px !important;
    background: transparent !important;
    border: none !important;
    border-radius: 2px 2px 0px 0px !important;}

.tabs-newtab-button:hover {
    background-image: -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.05) 95%),
    -moz-linear-gradient(rgba(255,255,255,0.0), rgba(255,255,255,.5) 95%)  !important;}

.tabbrowser-tab[pinned], .tabbrowser-tab[pinned]:not([selected]){
    margin-top: 2px !important;
    margin-bottom: 1px !important;
    margin-right: 0px !important;
    height: 24px !important;
    max-height: 24px !important;
    border: 1px solid transparent !important;
    background: transparent!important;}

.tabbrowser-tab[pinned][selected] {
    background-image: -moz-linear-gradient(-70deg,  rgba(255,255,255,0.85) 0%, rgba(255,255,255,0) 75%) !important;
    border-left: 1px solid rgba(60,67,60,0.3) !important;
    border-right: 1px solid rgba(60,67,60,0.6) !important;
    border-top: 1px solid rgba(60,67,60,0.3) !important;
    border-bottom: 1px solid rgba(0,7,0,0.6) !important;
    border-radius: 4px 4px 4px 4px !important;
    box-shadow: 0px 0px 2px #fff, 0px 0px 0px #999 !important;}

.tabbrowser-tab:not([pinned="true"]){
    margin-top: 2px !important;
    border-radius: 3px 3px 0px 0px!important;
    border: none !important;
    background-image: -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.03) 95%),
    -moz-linear-gradient(rgba(255,255,255,.0), rgba(255,255,255,.25) 95%)  !important;
    color: #ffffff !important; /* Default text-color on inactive tab is white (#ffffff), change to an another css-color below if desired: */
    font-family: Calibri !important; /* Default font is Calibri, change to an another font below if desired (ie Arial Narrow, Segoe UI, .... or just delete this row ...): */       
    font-size: 1.05em !important;
    font-weight:bold!important;
    text-shadow: #000000 1px 1px 0px !important;
    border-left: 1px solid rgba(96,122,105,0.3) !important;
    border-right: 1px solid rgba(96,122,105,0.3) !important;
    border-bottom: 1px solid rgba(255,255,255,0.6) !important;
    padding-left: 6px !important;
    padding-top: 0px !important;}

.tabbrowser-tab[selected]:not([pinned])[fadein] {
    color: #eee!important; /* Default text-color on active tab is light green (#E0FAE9), change to an another css-color below if desired: */
    border-bottom: none !important;
    background-image: -moz-linear-gradient(rgba(0,0,0,.02), rgba(255,255,255,.0) 95%),
    -moz-linear-gradient(rgba(0,0,0,.01), rgba(255,255,255,.0) 10%)  !important;
    border-left: 1px solid rgba(210,210,210,0.7) !important;
    border-right: 1px solid rgba(220,220,220,0.4) !important;
 
 
 box-shadow: 0px -1px 0px #eee, 0px -2px 0px #bbb, -1px 0px 2px #404040, 1px -1px 4px -1px #000000 !important;
    font-size: 1.30em !important;
    text-shadow: #000000 2px 1px 1px !important;
    padding-left: 2px !important;
    padding-top: 2px !important;}

.tabbrowser-tab:hover:not([pinned]){
    color: #ffffff !important; /* Default text-color on hover tab is white (#ffffff), change to an another css-color below if desired: */
    border-left: 1px solid rgba(224,224,224,0.3) !important;
    border-right: 1px solid rgba(245,245,245,0.35) !important;
    border-bottom: none !important;
    background: -moz-linear-gradient(top, rgba(255,255,255,0.15)100%, rgba(255,255,255,0.15) 100%) !important;
    box-shadow: 0px -1px 0px #e0e0e0,  0px 0px 1px #808080,  1px -1px 2px -1px #404040  !important;
    font-size: 1.15em !important;
    text-shadow: #000000 1px 1px 0px !important;
    padding-left: 4px !important;
    padding-top: 1px !important;}
    
/*Change tab's iconsize (related to choosed fontsizes above: 1em is equal) */
.tabbrowser-tab:not([pinned="true"]) .tab-icon-image {
    width: 1em !important;
    height: 1em !important;}
    
.tabbrowser-tab[selected]:not([pinned])[fadein] .tab-icon-image {
    width: 1em !important;
    height: 1em !important;}
    
.tabbrowser-tab:hover:not([pinned]) .tab-icon-image {
    width: 1em !important;
    height: 1em !important;}
    
/*Change size of app tabs, if in use */
/*
#TabsToolbar>.tabbrowser-tab[pinned], .tab-icon-image {
    width: 1.5em !important;
    max-width: 1.5em !important;
    max-height: 1.5em !important;}
*/
     
#TabsToolbar, #browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar {
    background: transparent !important;}

.toolbarbutton-1 > .toolbarbutton-menubutton-button, .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker, .toolbarbutton-1 {
    border: none !important;
    margin-right: 0px !important;
    margin-left: 0px !important;}

.toolbarbutton-1 > .toolbarbutton-menubutton-button:not(:hover), .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker:not(:hover), .toolbarbutton-1:not(:hover), .toolbarbutton-1[disabled="true"] {
    background: -moz-radial-gradient(rgba(255,255,255,.0), rgba(255,255,255,.0)) !important;
    box-shadow: none !important;}

#navigator-toolbox[iconsize="large"] #nav-bar toolbarbutton {
	margin: -6px 0px -6px 0px !important;
	padding: 0px 0px 0px 1px !important;}

#forward-button .toolbarbutton-icon[iconsize="large"]{
    height: 22px !important;
    min-width: 29px !important;
    max-width: 29px !important;}

#forward-button[disabled="true"] .toolbarbutton-icon[iconsize="large"] {
    height: 22px !important;
    min-width: 29px !important;
    max-width: 29px !important;}
	
#browser-border-end, #browser-border-start {
    display: none !important;}
	
#browser-bottombox {
    background-color: rgba(255,255,255,.0) !important;
    border: none !important;}
	
#addon-bar {
    -moz-appearance: none !important;
    background: none !important;
    border: none !important;}
	
#addonbar-closebutton {
    display: none !important;}
 
findbar, #FindToolbar {
  -moz-box-ordinal-group: 10 !important;
  position: static !important;
  height: auto !important;

  transition-delay: 0s, 0s, 0ms !important;
  transform: none !important;
  opacity: 1 !important;
  
  border-top: 1px solid ThreeDHighlight !important;
  border-bottom: 0 !important;
}

/* White icons - delete all below if standard (black) is preferred */
#main-window :-moz-any(#back-button, #forward-button, #reload-button, #stop-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button:not([status="active"]), #feed-button, #new-tab-button) > .toolbarbutton-icon
    {list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important; }

#main-window #alltabs-button
    {list-style-image: url("chrome://browser/skin/mainwindow-dropdown-arrow-inverted.png") !important; }

#main-window #tabview-button
    {list-style-image: url("chrome://browser/skin/tabview/tabview-inverted.png") !important; }

#main-window .tabs-newtab-button,
#main-window #TabsToolbar #new-tab-button > .toolbarbutton-icon
    {list-style-image: url("chrome://browser/skin/tabbrowser/newtab-inverted.png") !important; }

#main-window .scrollbutton-up,
#main-window .scrollbutton-down
    {list-style-image: url("chrome://browser/skin/tabbrowser/tab-arrow-left-inverted.png") !important; }
