@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sceper.eu") {

/* Top Categories */
#from-categories { display: none !important; }

/* Right Side-Bar Ads */
#text-1, #text-2, #text-3, #text-4, #text-5, #text-6, #text-7 { display: none !important; }

/* "Friendly Links" Right Side-Bar Ads */
#linkcat-1724 { display: none !important; }

/* iFrame Ads */
iframe { display: none !important; }

/* Comments */
#comment-wrap { display: none !important; }

/* Footer */
#footer, #footer-bottom { display: none !important; }

}
