css:
    @namespace url(http://www.w3.org/1999/xhtml);

    @-moz-document url-prefix("http://www.xanga.com/") {
    Body {background-image: none ! important; background-color: WHITE ! important; text-align: center; font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none;}
    a:link {color: #000000 ! important; text-decoration: underline; }
    a:link {color: #000000 ! important; text-decoration: underline; }
    a:visited {color: #CCCCCC; text-decoration: underline; }
    a:active {color: #BF0000; text-decoration: underline; }
    a:hover {color: #7A7A7A; text-decoration: underline; }
    .nav.link {color: #000000 ! important; text-decoration: underline; }
    a.nav:link {color: #000000 ! important; text-decoration: underline; }
    a.nav:visited {color: #CCCCCC; text-decoration: underline; }
    a.nav:active {color: #BF0000; text-decoration: underline; }
    a.nav:hover {color: #7A7A7A; text-decoration: underline; }
    .navselected.link {color: #000000 ! important; text-decoration: underline; }
    a.navselected:link {color: #000000 ! important; text-decoration: underline; }
    a.navselected:visited {color: #CCCCCC; text-decoration: underline; }
    a.navselected:active {color: #BF0000; text-decoration: underline; }
    a.navselected:hover {color: #7A7A7A; text-decoration: underline; }
    .footernav.link {color: #000000 ! important; text-decoration: underline; }
    a.footernav:link {color: #000000 ! important; text-decoration: underline; }
    a.footernav:visited {color: #CCCCCC; text-decoration: underline; }
    a.footernav:active {color: #BF0000; text-decoration: underline; }
    a.footernav:hover {color: #7A7A7A; text-decoration: underline; }
    p, center {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; }
    .smalltext {font-size: x-small; }
    LI {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; }
    UL {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; }
    TD {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; }
    TH {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; }
    HR {color: #666666;}
    table.banner { background-color: #ffffff; border: 1px solid #CCCC99; background-image: none; }
    table.header { width: 100%; background-color: #DF2020; border: 2px solid #858585; }
    table.header TH {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; font-weight: normal; border: none; }
    table.header TD {font-family: Courier; color: #000000 ! important; font-size: medium; text-decoration: none; border: none; }
    table.navigation { width: 100%; border: none; }
    table.footer { width: 100%; border: none; }
    table.main { width: 100%; border: none; }
    table.main TD, table.main TR { border: none; }
    table.search { color: #000000 ! important; background-color: #E6E6C3; border: 1px solid #CCCC99; }
    table.search TH { color: #000000 ! important; background-color: #E6E6C3; border: 1px solid #CCCC99; background-image: none; }
    table.search TD, table.search TR { color: #000000 ! important; background-color: #E6E6C3; border: none; background-image: none; }
    table.left { border: 1px inset #000000 ! important; }
    table.left TH { color: #D6D6D6; background-color: #5C5C5C; text-align: left; border: none; background-image: none; }
    table.left TD, table.left TR { color: #999999; background-color: transparent; text-align: left; border: none; background-image: none; }
    table.right { border: 1px inset #000000 ! important; }
    table.right TH { color: #D6D6D6; background-color: #5C5C5C; text-align: left; border: none; background-image: none; }
    table.right TD, table.right TR { color: #999999; background-color: transparent; text-align: left; border: none; background-image: none; }
    table.announcements { border: 1px solid #666666; background-image: none; }
    table.announcements TH { border: 1px solid #666666; border-bottom: 2px solid #666666; background-image: none; }
    table.announcements TD, table.announcements TR { border: none; background-image: none; }
    .button {font-family: Courier; border: 1px solid #666666; }
    .textfield {font-family: Courier; border: 1px solid #666666; }
    .pulldown {font-family: Courier; border: 1px solid #666666; }
    table.textad {border: 2px dotted #47C30B;}
    table.textad TD, table.textad TR { color: #000000 ! important; background-color: #F9F9F9; border: none; background-image: none; }
    .textad:link {color: #3366cc; text-decoration: underline; }
    a.textad:link {color: #3366cc; text-decoration: underline; }
    a.textad:visited {color: #3366cc; text-decoration: underline; }
    a.textad:active {color: #ff9900; text-decoration: none; }
    .blogheader {font-family: Courier; color: #000000 ! important; font-size: small; text-decoration: none; font-weight: bold; padding-bottom: 4pt; }
    }
