css:
/* NieNova - 2011 - JVC - CDV - SUNAMY */

@-moz-document url-prefix("http://www.jeuxvideo.com/profil/") { 

#cdv_page {
border-color: rgb(200,200,200) !important;
}

#cdv_header {
background: url("http://s2.noelshack.com/uploads/images/12469686086682_header_test_sunamy.png") no-repeat scroll left top #DDDDDD !important;
}

#rang span {
background: url("http://s2.noelshack.com/uploads/images/17151316756794_rubis_logo_sunamy.png") no-repeat scroll left top transparent !important;
}

#pseudo:after {
content: "- Skin By NieNova to Sunamy -";
text-transform: capitalize;
color: rgb(224,24,24);
font-size: 80%;
left: 425px !important;
top: 15px !important;
position: absolute !important;
}

#onglets li, #onglets li a, #onglets li a:hover {
color: rgb(224,24,24) !important;
}

#onglets li:not([class="on"]), #onglets li a {
background: url("http://s2.noelshack.com/uploads/images/10347367069809_onglet_sunamy.png") no-repeat top left !important;
}

#onglets li a:hover {
background: black !important;
-moz-border-radius: 2px 2px 0px 0px !important;
}

h2, #td_niveau strong, #td_pts strong {
color: rgb(150,30,30) !important;
}

.msg1 {
background-color: rgb(255,235,235) !important;
}

#pt_jeuonline li {
background-color: rgb(245,225,225) !important;
}

#nbpost img, #nbniv img {
background:url("http://s2.noelshack.com/uploads/images/20119983540426_nb_de_postes_sunamy.png") repeat-x scroll left bottom transparent !important;
}

}
