css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("notepad.mail.yahoo.com"),
               domain("notepad.yahoo.com")
 {
    /* First created: Wednesday, December 19 2007 05:37 AM UTC
       Last modified: Mon 02 May 2011 04:52:46 PM UTC
     *   Minor change: added second domain above.
    */

   /* td valign="top" width="100%" id="maincontent" style="padding:0;" class="options" */
    td#maincontent , td#maincontent>table td
      {
          background-color : rgb(0,0,0)    !Important;
                     color : rgb(84,84,84) !Important;
      }


    div#calhdr>a.print
      {
          background-color : #9DAF6F         !Important;
                 font-size : 3.1mm           !Important;
                     color : #FFFFFF         !Important;
      }

    textarea.tf
      {
          background-color : rgb(34,38,23)   !Important;
                     color : #D8F983         !Important;
        /* Does not work -- shoved scrollbar back from the right to the left by this much
           on FFx / IceWeasel   padding-right    : 16pt  */
              padding-left : 4px             !Important;
                    width  : 1005px          !Important;
      }

    /* Style the Notes listing too */
    table#datatable thead th ,
    table#datatable tbody td
      {
          font-size   : 8pt         !Important;
          font-family : "dejavu sans mono", monospace  !Important;
              color   : #97D0A8     !Important;
      }
    table#datatable tbody tr td a
      {
          color       : #FFCAB9     !Important;
          font-size   : 8pt         !Important;
          font-family : sans-serif  !Important;
      text-decoration : none        !Important;
      }

    table#datatable tbody tr td a:active ,
    table#datatable tbody tr td a:hover
      {
          color       : #D06B13     !Important;
      }

  /* End of Style */
}
