css:
/*======================================*/
/* All site */
@-moz-document
{
*{
  font-family: "Droid Sans","Microsoft Yahei",sans-serif !important;
}

pre, code, kbd, samp, var {
font-family: "Droid Sans Mono", "Microsoft YaHei", monospace !important; 
 font-size: 10pt !important;
 color: #088A08 !important;
}

}

/*======================================*/
/* Evernote */
@-moz-document domain("www.evernote.com")
{
 h1, h2{
font-size:15pt !important;
color:#666666 !important; 
 }

#note_frame, html{
font-size: 10.5pt !important;
line-height: 180% !important;
}

.Notebook, a, .a, .gwt-MenuBar .gwt-MenuItem {
font-size: 11pt !important;
}

.gwt-Label {
font-size: 11pt !important;
}

#list_view table .tagcol {
width:15% !important;
}

#toolbar_buttons * {
font-size: 10pt !important;
font-weight:400 !important;
}

.SidebarInfo {
display:none !important;
}

input, select, .Tags {
font-size:1em !important;
}

#view_previous_next {
right:5px !important;
top:1px !important;
}
}

/*======================================*/
/* Hootsuite */
@-moz-document domain("hootsuite.com")
{
.message .messageContent {
  font-size: 15px !important;
  line-height: 22px !important;
  color: #1b1b1b !important;
}

}

/*======================================*/
/* Twitter */
@-moz-document domain("twitter.com")
{
* {
  font-size: 10.5pt !important;
  line-height: 21px !important;
  color: #1b1b1b !important;
  }

}

/* ======================================*/
/* XSbook */
@-moz-document
  url-prefix("http://www.xsbook.net")
{
#content {
  font-size: 13pt !important;
  line-height: 31px !important;
  color: #181818 !important;
  width: 700px !important;
  }

   div.topad, table,span#adbanner_2,span#adbanner_3 {
   display:none !important;
   }

}

/* ======================================*/
/* Baishuku */
@-moz-document
  url-prefix("http://baishuku.com")
{
#content {
  font-size: 13pt !important;
  line-height: 31px !important;
  color: #181818 !important;
  width: 700px !important;
  }

   div#adtop1, div#adtop2,div#adbottom,div.style3,div#footer1 {
   display:none !important;
   }
}

/* ======================================*/
/* Mimi */
@-moz-document
  url-prefix("http://a.klmimi.com/")
{
table.row {
     font-size: 11pt !important;
     line-height: 16px !important;
    }
.t_msgfont, .t_msgfont table  {
     font-size: 13pt !important;
     line-height: 27px !important;
    }
table#table1, table#table8 {
   display:none !important;
   }
}
/* ======================================*/
/* Canadameet */
@-moz-document
  url-prefix("http://forum.iask.ca")
{
.alt1, .alt1Active
{
  font-size: 12pt !important;
  line-height: 26px !important;
}

}

/* ======================================*/
/* 163*/
@-moz-document domain("163.com")
{
p.summary, #endText p {
 font-size: 16px !important;
 line-height: 27px !important;
 color: #1e1e1e !important;
}

.f14bla li {
 font-size: 15px !important;
 color: #1e1e1e !important;
}
}
