css:
/*
* Author: Will (war59312@gmail.com)
* Description: Cleans up slashgear.com article pages.
*/

/* +++ changelog +++
May 1st, 2012
- hides "Story Timeline"
- fixes "Footer"
June 1st, 2011
- hides "Must Read Bits & Bytes"
March 31, 2011
- hides "Floating Twitter Ad Box"
March 15, 2011
- original release
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashgear.com") {

/* Hides Header */
#header, #prefooter { display:none !important }

/* Header Wrapper */
#headersliderwrapper { display:none !important }

/* "Categories" Header */
#access { display:none !important }

/* Header Ads */
#headerads { display:none !important }

/* "Trending" Ads */
.trending { display:none !important }

/* Twitter iFrame Ads */
iframe { display:none !important }

/* "Floating Twitter Ad Box" */
#sticky { display:none !important }

/* "Ad Box" */
.singleAdBox { display:none !important }

/* Un-needed Horisontal Line */
.horizontalLinelight, .clearFix { display:none !important }

/* "RELEVANT ENTRIES" Ad */
.singlemetaBox { display:none !important }

/* Right Side-Bar */
#sidebar { display:none !important }

/* "Subscribe Via" */
.postview_bar { display:none !important }

/* "Story Timeline" */
.related-posts { display:none !important }

/* Comments */
#disqus_thread, .comments-link { display:none !important }

/* "blog comments powered by Disqus" */
a[href="http://disqus.com"] { display:none !important }

/* "Must Read Bits & Bytes" */
.mustreadtitle, .mustreadbox, .mustreadtitlelink { display:none !important }

/* Footer */
#footer, #bottomfooter { display:none !important }

/* Move Content Up The Page A Bit And Center */
#content-wrap { margin: 0 25% 0 33% !important; }

}
