input {
    outline:none;
    transition: all 0.25s ease-in-out;
    -webkit-transition: all 0.25s ease-in-out;
    -moz-transition: all 0.25s ease-in-out;
    border:1px solid rgba(0,0,0, 0.2);
}

input:focus {
    box-shadow: 0 0 5px #FF004C;
    -webkit-box-shadow: 0 0 5px #FF004C; 
    -moz-box-shadow: 0 0 5px #FF004C;
    border:1px solid #FF004C; 
}


input[type=submit]
{ 
width: 80px; 
background-color: #006EFF;
border:0px;
color:#fff;
} 

input[type=text]
{ 
width: 140px; 
background-color: #FF006A; 
border:none; 
} 

a img {opacity: 1.0; -webkit-transition-duration:1s;}



a img:hover {opacity: 0.65; -webkit-transition-duration:1s;}
