@namespace url(http://www.w3.org/1999/xhtml);

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Bold.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Light.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Medium.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Medium.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Medium.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Medium.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v18.0.0/dist/Vazir-Medium.ttf') format('truetype');
  font-weight: 500;
}

.preview, .articlecontent, .post, .full_content, .excerpt {
font-family: tahoma, sans-serif !important;
font-size: 10pt;
direction: rtl;
text-align: right !important;
}

.preview b, .articlecontent b /*, b */ {
font-family: nazli, titr, serif !important;
font-size: 13pt !important;
}

/* h2 {
font-family: nazli, titr, serif !important;
font-size: 16pt !important;
} */

li.wall_post p, .msg_body, .smallholder, .about_message.party_section, textarea {
font-family: tahoma, sans-serif !important;
font-size: 10pt;
direction: rtl;
}

.smallholder, .dotted {
font-family: tahoma, sans-serif !important;
font-size: 9pt;
direction: rtl;
}

#shout {
height: 200px !important;
font-size: 18pt !important;
font-family: nazli !important;
color: #333 !important;
width: 355px !important;
}

.own_pic {
margin-left: 340px !important;
margin-top: -50px;
position: absolute !important;
}

/* .articlecontent {
font-family: dejavu sans, tahoma, sans-serif !important;
font-size: 10pt;
} */

.post_reply a {
display: block;
}

.padded {
text-align: left;
/* width: 100% !important; */
}

.largepadded {
text-align: left;
}

.user_pic {
position: absolute !important;
display: block;
left: auto !important;
right: 0 !important;
}

.post_content h6 {
text-align: right !important;
display: block;
}

.post_content h6 a {
float: right;
padding-left: 5px;
}
.post_content p {
padding-bottom: 1px !important;
}

#citizen_feed .wall_post .second_actions, #citizen_feed .wall_post .second_actions_comments {
right: auto !important;
left: 0 !important;
}


.resizable-textarea textarea {
font-family: tahoma, sans-serif !important;
font-size: 10pt;
direction: rtl;
position: relative;
margin-right: 50px !important;
margin-top: 30px !important;
width: 600px !important;
}

.x {
font-size: 18pt !important;
}

.messages {
direction: rtl !important;
}


#showAllBuildings {
margin-top: 20px;
}

.sIFR-replaced {
width: 180pt !important;
font-size: 14pt !important;

}
