/* +++ changelog +++
April 20th, 2013
- "Subscribe Now Footer Ad"
Jan 19th, 2013
- Hides "Header" Again
- Hides "valetTools"
May 20th, 2012
- fixed again
Oct 16th, 2011
- Hides "Join The Discussion"
March 18th, 2011
- Re-Hides "Related Stories & Related Videos"
March 18th, 2011
- original release
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://online.wsj.com/article") {

/* Header */
#hat_div, .header, .form_factor_nav, .wsjMainNav, .wsjSubnav, #newsreeliframe, .gHeader_c { display: none !important; }

/* Right Side-bar */
div.col4wide.margin-left.colOverflowTruncated { display: none !important; }

/* "Article, Stock Quotes, Comments, More In ..." */
.art_tabbed_nav { display: none !important; }

/* "Save This" */
.aToolUnit.aToolType-dropdown { display: none !important; }

/* "Twitter, Digg, More.." */
li.aToolUnit.aToolType-social { display: none !important; }

/* "valetTools" */
#valetTools-top, #valetTools-bottom { display: none !important; }

/* "By Line" */
.byline { display: none !important; }

/* "More In..." */
#morein_former, .more { display: none !important; }

/* "Article Tools" */
div.articleTools_bottom { display: none !important; }

/* Add A Comment */
#leaveComment{ display: none !important; }

/* Related Stories, Related Videos */
#related_stories_module, .relatedArticlesAuto { display: none !important; }

/* "Sponsored Links" And Other iFrame Based Ads */
iframe { display: none !important; }

/* Editor's Picks */
#carouselHighlightsLeft { display: none !important; }

/* "Join The Discussion" */
#morein { display: none !important; }

/* "Subscribe Now Footer Ad" */
.subscribeNowSmallBar { display: none !important; }

/* Footer */
#footer { display: none !important; }

/* "Article Type" */
.articleSection.first { display: none !important; }

/* Print Mode Header/CopyRight */
.printSummary.pfHeader.col6wide { display: none !important; }

/* Print Mode Footer/CopyRight */
#article_pagination_bottom, #printModeFooterAd, .printSummary.pfFooter { display: none !important; }

/* Ad That Shows Up When Printing */
#printModeAd { display: none !important; }

/* Widen Story Div To 100% Width */
#article_story { width: 100% !important; }

}
