css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("book.cakephp.org")
{
	/* =============== Site wide ===============  */
	.default {
		color: #B5B5B5 !important;
	}
    
	body {
		background-color: #2C2C2C !important;
	}

    ul > li[class='toctree-l1'] > a, ul > li[class='toctree-l2'] > a, ul > li[class='toctree-l3'] > a {
        color: #9a9a9a !important;
    }
    
    div.page-contents {
        background-color: #1F252B !important;
    }
    
    div.page-contents > h3 {
        color: #9a9a9a !important;
        /*text-shadow: 3px 3px 8px rgba(182, 191, 249, 0.2);*/
    }
    
    div.page-contents > ul > li > a, div.page-contents > ul > li >  ul > li a {
        color: #929292 !important;
        /*text-shadow: 3px 3px 5px rgba(182, 191, 249, 0.3);*/
    }
    
    h1 { color: #9a9a9a !important; }
    
    h2 { color: #9a9a9a !important; }
    
    h3 { color: #9a9a9a !important; }
    
    p { color: #9a9a9a !important; }
	
    div.admonition.note {
        background-color: #1F252B;
    }
    
    div.admonition.note p{
        color: #BC895D !important;
    }
    
    div.admonition.tip{
        background-color: #1F252B;
    }
    
    div.admonition.tip p{
       color: #789A89 !important;
    }
    
    div.versionchanged {
       background-color: #1F252B;
    }
    div.versionchanged p{
        color: #ADAE55 !important;
    }
    
    div.deprecated {
       background-color: #1F252B;
    }
    
    div.deprecated p{
        color: #AFAD56 !important;
    }
    
    div.versionadded {
       background-color: #1F252B;
    }
    
    div.versionadded p{
        color: #AFAD56 !important;
    }
    
    div.admonition.warning{
        background-color: #1F252B;
    }
    
    div.admonition.warning p{
        color: #C82334 !important;
    }
    
    div.offline-download.container {
        background-color: #1F252B;
    }
    
    ul li {
        color: #AEC562;
    }
    
	ol li {
        color: #AEC562;
    }

    span.pre{
        color: #FFFFFF;
        background-color: #3E4650;
    }
    
    tt span.pre{
        color: #5F6B78;
        background-color: #2C2C2C;
    }
    
    dt big {
        color: #9A9A9A;
    }
    
    dt em {
        color: #F0F0F0 !important;
    }
    
    tt[class='descclassname'] {
        color: #9A9A9A !important;;
    }
    
    tt[class='descname'] {
        color: #9A9A9A !important;;
    }
    
    em.property{
        color: #DF910E !important;
        background-color:#3E4650 !important;
        text-shadow: 0px 1px #000 !important;
    }
    
	/* ============= Code-section ==============  */
    .highlight pre {
        background-color: #333333;
    }
    
    span.nx { color: #C44F24 !important;}
    span.na { color: #F0F0F0 !important;}
    span.s1 { color: #AEC562 !important;}
    span.k { color: #FFFFFF !important;}
    span.p { color: #FFFFFF !important;}
    span.nv { color: #BC895D !important;}
    span.mi { color: #BC895D !important;}
    span.s2 { color: #9EC8E0 !important;}
    span.nb { color: #CBD8E8 !important;}
    span.cm { color: #898989 !important;}
    span.c1 { color: #898989 !important;}
    span.o { color: #898989 !important;}
    span.mf { color: #BD8A5D !important;}
    span.x { color: #ABABAB !important;}
    span.nt { color: #E6D274 !important;}
    span.optional { color: #9A9A9A !important;}
    span.err { color: #777777 !important; border: 0px !important; }
    
    p tt
    {
       font-size: 1.00em !important; background-color: #2C2C2C !important;
       color: #AEC562 !important;
    }
    
    td tt
    {
       font-size: 1.20em !important;
       color: #000000 !important;
    }
    
    table tr th {
        border: solid 1px #FFFEF2 !important;
        background-color: #ABABAB !important;
        color: #252C33 !important;
    }
    
    tbody .row-even {
        background-color: #3A434D;
    }
    
    tbody .row-even td {
       color: #B6B9BE !important;
    }
    
    table tbody tr:nth-child(2n) {
        background-color: #252C33;
    }
    
    table tbody tr:nth-child(2n) td {
        color: #5F6B78 !important;
    }
    
    code[class='descclassname'] {
        color: #B6B9BE !important;
    }

    code[class='descname'] {
        color: #AEC562 !important;
    }
}
