css:
@-moz-document domain('www.gutenberg.org') {
html {
  background-color: #333;
}

body {
  font-size: normal;
  width: 40em;
  margin: 0 auto 0 auto;
  padding: 0 13.3em 0 13.3em;
  font-family: "Minion Pro", "Crimson Text", Palatino, serif;
  text-align: left;
  line-height: 1.4em;
  color: black;
}

p {
  text-indent: 1em;
  margin: 0 0 0.35em 0;
}

a:link, a:hover, a:visited {
  color: black;
  text-decoration: none;
  border-bottom: 1px dotted black;
}

a:hover {
  border-bottom: 1px solid black;
}

table {
  font-size: normal;
}
}
