css:
/*******************************/
/* Arsenal skin                */
/* Date: 20/03/2011            */
/* Made by: rei85              */
/*******************************/

@-moz-document domain("hattrick.org") {


#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
        /*background: url(page/top.png) bottom left no-repeat !important;*/
}

#footer a
{
        color: white !important; 
}

#hattrick,#hattrickNoSupporter 
{
        margin:auto!important; 
        position:relative;
        left:-5px;width:993px!important;
}

#ticker
{
	position: absolute !important;
	top: 35px !important;
	left: 360px !important;
}


#ticker div a
{
	padding-bottom: 2px !important;
	color: #000000 !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}

#shortcuts
{
	margin-top: 0px !important;
	margin-left: 0px !important;
}

#teamLinks
{
        color: #ffffff !important;
        margin-top: 30px !important;
	margin-left: 0px !important;
}

div#teamLinks a
{
        color: #ffffff !important;
}

/*Users online - week - date - time*/

div[id="online"] 
{
        color: white !important; 
        float:right!important;
        margin-right:0pt!important;
        margin-top:25pt!important;
}
div[id="time"]
{
        color: white !important;
        float:right!important;
        margin-right:8pt!important;
        margin-top:25pt!important;
}

/*Page background*/
body
{  
  background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/bg_arsenal-1.gif) !important;
}

/*centre title*/
h1 
{
        color: #aa0000 !important;
        border-bottom: solid thin darkgrey !important;
        background: #eeeeee !important;
}

/*all boxHead*/
h2 
{
        /*color: white !important;*/
        /*border-bottom: solid thin #eeeeee !important;*/
        /*background: #c70000 !important;*/
}

/*centre news*/
.boxBody h2 
{ 
        color: #aa0000 !important;
        border-bottom: solid 1px black !important;
}


/*-----------boxHeads-----------*/

/*Left menu*/
.subMenuBox .boxHead
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/head_box2.jpg) !important;
}

.subMenuBox .boxHead .boxLeft
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/head_box2.jpg) !important;      
}

/*Centre box*/
.main .boxHead h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/head_box2.jpg) !important;  
}

/*Right menu*/
.sidebarBox h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/head_box2.jpg) !important;
}

/* Press, GB, Team events */
.main .mainBox h2 
{
	border-bottom: solid 1px #000000 !important;
	background-color: #fff !important;
	color: #aa0000 !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}


/*Central header paths*/

h2 a 
{
        color: #ffffff !important;
}

h2 a:hover 
{
        color:#e2da00 !important;
}


/*News and team events date*/

.date
{
	color: #000000 !important;
	padding-right: 8px;
	font-weight: 400;
}

/*Left Menu titles*/
h3 
{
        color: #aa0000 !important;
        border-bottom: solid thin #808080 !important;
        background: #eeeeee !important;
}

/*General Links*/
a
{
        color: #aa0000 !important;
	text-decoration: none !important;
}

/*----subMenu and sidebar links----*/
.subMenuBox a, .subMenuBox a, .sidebarBox a 
{
	text-decoration: none !important;
	color: black !important;
}

/*Bot teams link*/

div a[class="shy"] 
{
        color: gray !important;
}


/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}

#stageLogo {
        background: none !important;
}

/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}


/* central page background */
#content
{
        background-image: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/arsenal_cbg1.gif) !important;
        background-color: #ffffff !important;

}

/*Image header (banner)*/
#header 
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/arsenal_banner4.jpg) !important;
	height: 125px !important;
	padding-top: 50px !important;
}

/*--------Top menu settings--------*/
#menu
{
	height: 30px !important;
        width: 975px !important;	
        margin-top: 5px !important;
        margin-left: -14px !important;
        bottom: 0px !important;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/top_menu.jpg) !important;
}

#menu a
{
	line-height: 30px !important;  
        color: #ffffff !important;
        /*-moz-border-radius: 2% !important;*/        
        /*background: #808080 !important;*/
}

div#menu a:hover 
{
        color:#FFFFFF !important; background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/over_menu-1.jpg) !important;
}

a:hover
{
	color: #000000 !important;
}

/*bottom white line*/
div[id="bottom"]
{
        height: 19px !important;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/arsenal_cbg2.gif) !important;
}


/*---------Forum---------*/
.cfHeader
{
        background:  url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/head_box2.jpg) !important;
}

#cfPollHeader
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/head_box2.jpg) !important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
}

.cfWrapper
{
	border: solid 1px #000000 !important;
	margin: 0 0 12px 0;
}

.cfWrapper div.signature a, div.signature-trunc a, div.signature-expand a {
    color: #999999 !important;
    text-decoration: underline !important;
}

.cfHeader a
{
	color: #ffffff !important;
	font-weight: bold;
	text-decoration: none;
}

.cfWrapper a
{
	text-decoration: none;
        /*color: white !important;*/
}

.cfUserInfo a
{
        /*color: #de4747 !important;*/
}

.cfMessage a
{
	/*color: #de4747 !important;*/
}

.folderHeader a:link 
{
	color: #000000 !important;
	font-weight: bold !important;
}

.cfFooter a
{ 
        color: black !important;
}


.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/cross_small.png) no-repeat !important;
}

.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tick_small.png) !important;
}

.folderItem div.unignore:hover
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tickGreen_small.png) !important;
}

/*Ads section*/

div[class="insertHeader"]
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/sevenmenu.png) !important;
}

div[class="insertFooter"]
{
        background: #ffffff !important;
}


/*shortcuts*/

img.scNewMail 
{
	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;
}


/* MatchLineup */
.name a
{
	color: #277f31 !important;
}

/* Tabs Match orders*/
.ui-tabs .ui-tabs-nav li a { float: left; text-decoration: none; padding: 4px 10px 3px 10px; color:#eee; text-decoration:none;  color: white !important;}

/* Tabs Match line up */
.LinkTabActive, .LinkTabNotActive {
    background-color: #2E741B;
    color: #FFFFFF !important;
    float: left;
    font-size: 1em;
    font-weight: 700;
    padding: 0 4px;
    text-decoration: none;
}

/*Foxtrick pop-up menu*/

span.myht1:hover div.myht2 {
display: inline;
position: absolute;
left: 20px;
background-color: #fff !important;
border: solid 1px #000000 !important;
padding: 0px;
z-index: 999
}

div.playerInfo {
overflow: visible !important;
}

span.myht1 table>tr>td>a {
font-weight: normal !important;
text-decoration: underline !important;
color: #cd0403 !important;
}

span.myht1 table>tr>td:hover {
background-color:#ccc !important;
}

div[class*="ad_"]
{
        display:none !important;
}

}
