/* CSS Document */

@-moz-document domain("www.freekick.org")
{

/*GLOBAL*/
html{
    background: none #000 !important;}

body{
    background: none #000 !important;
    color: #FFF !important;
    font-family: Verdana !important;}

a{color: #FFF !important;}
a.club{color: #EE6 !important;}

div.container{
    background: none #000 !important;}

div#page-wrap{
    margin: auto !important;
    background: none #000 !important;}

/*-------------------------*/
/*FRONT PAGE (BEFORE LOGIN)*/

#form-login li label{
    color: #FFF !important;}

h2.grey, .freekick{
    color: #8D7 !important;}

.signup, div.bg, div.bg-dwn, .quote-l, .quote-r{
    display: none !important;}

.profile-name, .profile-answ .quest{
    color: #8D7 !important;}
.profile-answ div{
    background-color: #040 !important;}

.answ-content{
    border: 0 !important;}

/*-----------------------------------*/
/*TOP PART - LOGO, ADDS AND SITE INFO*/

div#top-wrap{
    background: none #040 !important;}
div.darkGrey{
    background: none #000 !important;}

div#top-banner{
    display: none !important;}

div#top-info, input#clock-input{
    background-image: none !important;
    color: #FFF !important;
    font-weight: bold !important;}

/*--------------------------------------------------------------*/
/*TOP MENU - NAVIGATION, RULES, MANUAL, FORUM, SEARCH AND LOGOUT*/

div#blue-bar{
    background-color: #040 !important;
    -moz-border-radius: 0 0 12px 0 !important;}

p#blue-nav{
    background-image: none !important;}

.header-sprite{
    background-image: none !important;}

div#left-wrap{
    background: none #040 !important;
    margin: 14px 10px 0px 0px !important;
    -moz-border-radius: 5px 30px 30px 5px !important;}

#lang-select{
    width: 170px !important;
    background: none #040 !important;
    -moz-border-radius: 0 0 0 12px !important;}

.lang-select-crn{
    background-image: none !important;}

#languageSelector ul{
    background-color: #040 !important;
    -moz-border-radius: 0px 0px 0px 20px !important;
    border: 2px solid #000 !important;
    list-style: none !important;}

#languageSelector li{
    background-image: none !important;}

#languageSelector li a{
    color: #FFF !important;
    border-left: 0 !important;}

#languageSelector li a:hover{
    background-color: #040 !important;}

/*----------------------------------------------------------------------------*/
/*LEFT MENU - LOCKER ROOM, COMPETITION, OFFICE, COMMUNITY, SETTINGS AND STATUS*/

div.menu-container{
    margin-bottom: 0 !important;}

.menu-sprite{
    background: none !important;}
ul.makeMenu a, li{
    border: none !important;}

ul.makeMenu li{
    background-image: none !important;}

ul.makeMenu ul{
    background-color: #040 !important;
    border: 2px solid #000 !important;
    -moz-border-radius: 0px 12px 0px 12px !important;}

ul.makeMenu a:hover{
    background-color: #040 !important;
    text-decoration: underline !important;}

.green-stripe{
    background-image: none !important;}

.settings-header{
    color: #8D7 !important;}

.status-left a{
    color: #FFF !important;
    border-left: 0 !important;}

.status-left li:hover, .menu-status ul li:hover{
    background-color: #040 !important;}

.menu-status a{
    border-left: 0 !important;}

.status-left p{
    color: #FFF !important;
    font-weight: bold !important;
    font-size: 10px !important;}

.status-right{
    color: #AAA !important;}

.off{color: #F33 !important;}
.on{color: #6F4 !important;}

/*-------------------------------*/
/*MAIN CONTENT - GENERAL SETTINGS*/

#main-green-wrap{
    margin-top: 14px !important;
    -moz-border-radius: 5px 30px 30px 5px !important;
    background: none #040 !important;}

#content-l div.left a img{
    width: 200px !important;
    margin-left: -15px !important;}

#content-l div.left a img.flag{
    width: 18px !important;
    margin-left: 0px !important;}

#green-round, #white-content, .rigth-crn{
    background: none !important;}

#content-r, #content-l{
    border: 0px !important;}

#content-l{
    width: 535px !important;}

#content-r{
    color: #FFF !important;
    width: 180px !important;}

#content-r ul a{
    color: #EE6 !important;}

.green-head, .blue-head, .red-head{
    background: none #170 !important;
    -moz-border-radius: 10px 10px 10px 10px !important;
    -moz-box-shadow: 0 0 2px rgba(255, 255, 255, 0.4) inset,
                     0 4px 6px rgba(255, 255, 255, 0.4) inset,
                     0pt 11px 0 -2px rgba(255, 255, 255, 0.2) inset,
                     0pt 13px 8px rgba(0, 0, 0, 0.3) inset,
                     0pt 1px 0px rgba(0, 0, 0, 0.2) !important;}

.green-title, .blue-title, .red-title{
    background: none !important;
    -moz-border-radius: 10px 10px 10px 10px !important;}
.green-crn-r, .green-crn-re, .blue-crn-r, .blue-crn-re, .red-crn-r, .red-crn-re{
    background: none !important;
    -moz-border-radius: 10px 10px 10px 10px !important;}

.age-old{
  color: #CD3B3B !important;
  font-weight: bold !important;}

.age-senior{
  color: #FFF !important;}

.age-youth, .age-youth-0{
  color: #7A7 !important;}

.age-youth-1, .age-youth-2, .age-youth-3, .age-youth-4{
  color: #DE6 !important;}

.awesome{
    color:#D61 !important;
    font-weight:bold !important;}
.brilliant{
    color:#F83 !important;
    font-weight:bold !important;}
.superb{
    color:#F96 !important;
    font-weight:bold !important;}
.excellent{
    color:#F9C !important;
    font-weight:bold !important;}
.good{
    color:#C3C !important;
    font-weight:bold !important;}
.decent{
    color:#FFF !important;
    font-weight:bold !important;}
.weak{
    color:#7A7 !important;
    font-weight:normal !important;}
span.poor, td.poor{
    color:#CC3 !important;
    font-weight:normal !important;}
span.awful, td.awful{
    color:#E10 !important;
    font-weight:normal !important;}

.abovepot{color: #8D7 !important;}
.pot{color: #A86 !important;}
.drop{color: #F33 !important;}

.section-head, .section-head a{
    color: #EE6 !important;
    font-family: Georgia !important;
    font-size: 16px !important;
    font-weight: normal !important;
    text-shadow: black 1px 1px, black -1px -1px, black -1px 1px, black 1px -1px;}

.red-stripe{
    background-image: none !important;}

.highlightable{
    text-align: center !important;}

.income{color: #8D7 !important;}
.bid-inactive{color: #8D7 !important;}

div.btm-shadow{
    display: none !important;}

.featured, .featured table{
    border: none !important;
    background-color: #040 !important;}

.floating-row-header{
    background: none #040 !important;}

.versus{
    color: #FFF !important;}

.quick-match-feature{
    font-size: 16px !important;}

/*TABLES*/

th, th a{
    background: none #040 !important;
    text-align: center !important;
    color: #8D7 !important;}

#standings th{
    padding: 0 0 0 0  !important;}

.dots, .plain-dotted-list li, .plain-list li, .dotted{
    background: none !important;
    border-bottom: 0px !important;}

.dotted td, .dotted th, .dotted tr, .highlightable td, .highlightable th{
    border-bottom: 0px !important;}

.odd, .odd td, .alternate-odd td{
    background: none #050 !important;}

.even, .even td, .alternate-even td{
    background: none #040 !important;}

tr{
    background-image: none !important;}

.odd-marked td, tr.odd-marked td, .even-marked td, tr.even-marked td, tr.marked td{
    background: none #030 !important;}

tr.promotion-demotion-none, tr.promotion-demotion-qualification, tr.promotion-demotion-automatic{
    display: none !important;}

table.int-fixtures tr td.even, table.int-fixtures tr td.odd{
    background-color: #040 !important;}

.brdRGrey{
    border-right: 1px solid #666 !important;}

tr.greyLine td{
    background-image: none !important;}

.letter-count{
    color: #EEE !important;}

.moments td{
    background: none !important;}
.moments a{
    color: #EE6 !important;
    font-weight: bold !important;}

.financesSum td, .financesMain td{
    background-color: #040 !important;
    border: 0 !important;}

table.int-fixtures th.fk_table_header{
    background-color: #040 !important;
    border: 0 !important;}

.pyramid-closebrace-green-size-0, .pyramid-closebrace-grey-size-1, .pyramid-closebrace-green-size-2, .pyramid-closebrace-blue-size-4, .pyramid-closebrace-red-size-8{
    background-image: none !important;}

/*****GENERAL-TABS*****/

div#main-tabs li.tab-hidden, div.players-tabs, div.players-tabs li{
    background: none #040 !important;
    margin: 0px !important;
    font-size: 10px !important;
    font-family: Verdana !important;
    font-weight: normal !important;}

div#main-tabs li.tab-hidden a:hover, div.players-tabs li a:hover{
    background-image: none !important;
    color: #FFF !important;
    text-decoration: underline !important;}


div#main-tabs li.tab-hidden a, div.players-tabs li a{
    background-image: none !important;
    color: #FFF !important;
    text-decoration: none !important;
    font-weight: bold !important;}

div#main-tabs li.tab-active{
    background-image: none !important;
    margin: 0px !important;
    background-color: #170 !important;
    -moz-border-radius: 12px 12px 12px 12px !important;
    font-family: Verdana !important;
    font-size: 11px !important;}

div.players-tabs li.players-selected{
    background: none !important;}


div#main-tabs li.tab-active a, div.players-tabs li.players-selected a{
    background-image: none !important;
    color: #EE6 !important;
    text-decoration: none !important;
    font-weight: bold !important;}


div.players-wrap{
    background-image: none !important;
    border-top: 0px !important;}

div.btm-shadow{
    background-image: none !important;
    border-bottom: 2px solid #333 !important;}

div#menu-footer{
    font-size: 10px !important;}

p#footer-tabs span{
    background-image: none !important;}

p#footer-tabs span.leftmost{
    background-image: none !important;
    margin-left:0px !important;
    padding-left:0px !important;}


/*****END-GENERAL-TABS*****/
/*END - MAIN CONTENT - GENERAL SETTINGS*/

/*************/
/*PLAYER PAGE*/

.player-column-l{
    width: 19% !important;}

div.player-column-c table.dotted tbody tr th{
    margin: auto !important;}

td.training-training-days{
    color: #EE6 !important;
    text-align: right !important;
    font-size: 8px !important;}

td.header{
    color: #EE6 !important;
    background-color: #170 !important;}
.dotted td{
    background: none !important;}

/*********/
/*TACTICS*/

div.blue-round{
    background-color: #040 !important;
    -moz-border-radius: 12px 12px 12px 12px !important;}

div.roundtop, div.roundbottom{
    background-image: none !important;}

div.roundtop img.corner, div.roundbottom img.corner{
    display: none !important;}

div.inner-round span{
    margin: 5px !important;}

div.players-tabs ul li{
    padding-bottom: 4px !important;}

#team-order-info{
    background: #050 !important;
    border: 0 !important;}

#existing_co th.minimal{
    padding-left: 2px !important;
    padding-right: 2px !important;}

.co-inactive{
    background: #222 !important;
    color: #AAA !important;}
.lineup-invalid{
    background-color: #F00 !important;}
.lineup-ok{
    background-color: #170 !important;}

/**************/
/*MATCH REPORT*/

div.green-bl, div.green-br, div.green-tr, div.green-tl{
    background-image:none !important;
    background-color: #040 !important;}

div.blue-bl, div.blue-br, div.blue-tr, div.blue-tl{
    background-image:none !important;
    background-color: #050 !important;}

div.match-report, div.live-match-report{
    background-color: #040 !important;}

div.match-report ul.odd, div.live-match-report ul.odd{
    background-color: #050 !important;}

div.match-report ul.even, div.live-match-report ul.even{
    background-color: #040 !important;}

li.last{
    background-image: none !important;}

.matchreport-club{
    color:#000 !important;
    font-weight:bold !important;}

.matchreport_masterful, .matchreport_unbelievable, .matchreport_legendary{
    color:#F66 !important;
    font-weight:bold !important;}

.matchreport_awesome{
    color:#D61 !important;
    font-weight:bold !important;}

.matchreport_brilliant{
    color:#F83 !important;
    font-weight:bold !important;}

.matchreport_superb{
    color:#F96 !important;
    font-weight:bold !important;}

.matchreport_excellent{
    color:#F9C !important;
    font-weight:bold !important;}

.matchreport_good{
    color:#C3C !important;
    font-weight:bold !important;}

.matchreport_decent{
    color:#FFF !important;
    font-weight:bold !important;}

.matchreport_weak, .poor, .awful{
    color:#7A7 !important;
    font-weight:normal !important;}

.matchreport_poor{
    color:#CC3 !important;
    font-weight:normal !important;}

.matchreport_awful{
    color:#F00 !important;
    font-weight:normal !important;}

.matchreport_dreadful{
    color:#9C0000 !important;
    font-weight:normal !important;}

.match-kit-small{
    width: 90px !important;}


/**************/
/*NEWS, PRESS RELEASES, GUESTBOOK, MESSAGES*/

.news-items-header, .small-news-header{
    background: none #040 !important;}
.news-items-header-title{
    border-bottom: 0 !important;}
.news-items-release-date{
    color: #FFF !important;}
.news-items-release-date a, .small-news-date, .small-news-time{
    color: #AAA !important;}

tr.guestbook-comment{
    color: #8D7 !important;}

.news-title, .guestbook-title{
    background-color: #040 !important;
    color: #FFF !important;}

.news-title a, .questbook-title a{
    color: #FFF !important;}
.news-date, .news-time{
    color: #170 !important;}

ul li.message-header, ul.tutorial-tasks li{
    background: none #040 !important;}
li.message-content a{
    color: #EE6 !important;}

}
