/* | Wordpress Self-Hosted Admin Bar Remix - Blue v1.0:   |
   | A blue theme for the self hosted Wordpress admin bar |
   | by pikahyper - 2011                                  |
   | -----------------------------------------------------|
   | last modified: 03/22/2011                            |
   |______________________________________________________| */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yoururl.com") {

#wpadminbar .quicklinks li#wp-admin-bar-my-account-with-avatar > a {
background: transparent url(data:image/png;base64,%3D%3D) no-repeat scroll top left !important; }

#wpadminbar .quicklinks > ul > li:hover > a {
border-left-color: #0098FF !important; }

#wpadminbar {
background-color: #0098FF !important;
background-image: -moz-linear-gradient(center bottom , #0098FF, #D3F2FC) !important; }

#wpadminbar .quicklinks > ul > li > a {
border-right: none !important;
border-left: 1px solid #0098FF !important; }

#wpadminbar .quicklinks a, #wpadminbar .shortlink-input {
color: #ffffff !important; }

#wpadminbar .quicklinks li:hover, #wpadminbar .quicklinks .selected {
background: #40b2ff !important; }

#wpadminbar .quicklinks .menupop ul, #wpadminbar .shortlink-input {
background: #D3F2FC !important;
border-color: #0098FF !important; }

#wpadminbar .quicklinks .menupop ul li a {
color: #555555 !important; }

#wpadminbar .quicklinks .menupop ul li:hover > a {
color: #ffffff !important; }

#wpadminbar #adminbarsearch .adminbar-input {
width: 200px !important;
border: 1px solid #0098FF !important;
background-color: #D3F2FC !important; }

#wpadminbar #adminbarsearch .adminbar-button {
-moz-box-shadow: 1px 1px 1px #D3F2FC inset !important;
background: -moz-linear-gradient(center bottom , #0098FF, #D3F2FC) !important; }

#wpadminbar #adminbarsearch .adminbar-button:active {
-moz-box-shadow: 1px 1px 1px #D3F2FC inset !important;
background: -moz-linear-gradient(center bottom , #D3F2FC, #D3F2FC) !important; }

#wpadminbar #adminbarsearch .adminbar-button {
border-color: #aaaaaa !important; }

}
