style_setting_options.value:
/*remove Downloads label*/
#downbarHolder>#downbarMainMenuButton>label{display:none!important;}
/*change button labels to icons*/
#downbarClearButton>label,#downbarToMiniButton>label{-moz-binding:url("chrome://global/content/bindings/button.xml#button-image")!important;list-style-image:url("data:image/png;base64,")!important}
#downbarClearButton>label{-moz-image-region:rect(0 32px 16px 16px)!important;}
#downbarToMiniButton>label{-moz-image-region:rect(0 16px 16px 0)!important}
/*fix jumpyness from button icons*/
#downbarClearButton,#downbarToMiniButton,#downbarClearButton:hover,#downbarToMiniButton:hover{padding:2px!important;  background: -moz-linear-gradient(#FBFCFD, #E6F0FA 50%, #DCE6F4 50%, #DDE9F7) !important;}
/*remove button icon borders*/
#downbarHolder>*{border:none!important}

.db_downbar {
padding: 2px !important;
text-shadow: 0 1px 0 rgba(0,0,0,.75) !important;
font: bold 1em Arial !important;
  background: -moz-linear-gradient(#FBFCFD, #E6F0FA 50%, #DCE6F4 50%, #DDE9F7) !important;
}


.db_progressVbox {
padding: 0 2px 0 0 !important;
text-shadow: 0 0 0 !important;
}

#db_finTipLeftImg, #db_finTipRightImg, #db_finTipMiddle, #db_progTipLeftImg, #db_progTipRightImg, #db_progTipMiddle, #db_finTipImgPreviewBox {
   -moz-appearance: -moz-win-glass !important; 
   background:transparent !important; 
   list-style-image: none !important;
   border: none !important;
   /*background-color: rgba(255,255,255,0.25) !important;*/
   color: black !important;
   text-shadow: 0px 0px 6px white, 0px 0px 6px white, 0px 0px 6px white !important;
}

.db_downbarPopup, .fintip_transparent, .fintip_opaque, .progresstip_transparent, .progresstip_opaque {
color: black !important;
-moz-appearance: -moz-win-glass !important ;
background: transparent !important;
border: none !important;
}

#_dlbar_finTipContent,#_dlbar_progTipContent{
    background-color: rgba(179,230,255,.35)!important;
    background-image: -moz-linear-gradient(rgba(250,253,255,.9), rgba(250,253,255,.75) 20px, rgba(250,253,255,.70) 39px, rgba(250,253,255,0) 41px, rgba(250,253,255,0))!important;
}

.db_pauseStack,
.db_progressStack,
.db_finishedHbox,
.db_notdoneHbox {
background: none !important;
padding: 0 !important;
border: 0 !important;
margin: 2px !important;
border-radius: 2px !important;
text-shadow: 0 1px 0 rgba(255,255,255,.3) !important;
box-shadow:
  inset 0 13px 15px -8px rgba(0,0,0,.15),
  inset 0 1px 1px 0 rgba(0,0,0,.3),
  inset 0 0 0 1px rgba(0,0,0,.4),
  0 1px 0 rgba(255,255,255,.2)
  !important;
}

.db_pauseStack { background-color: #fffea2 !important; }
.db_progressStack { background-color: #aad0ff !important; }
.db_finishedHbox { background-color: #adf9ab !important; }
.db_notdoneHbox { background-color: transparent !important; }

.db_downbar > :-moz-window-inactive {
box-shadow:
  inset 0 13px 15px -8px rgba(0,0,0,.15),
  inset 0 1px 1px 0 rgba(0,0,0,.1),
  inset 0 0 0 1px rgba(0,0,0,.3),
  0 1px rgba(255,255,255,.2)
  !important;
}

.db_progress-bar {
background-color: rgba(255,255,255,.2) !important;
border-radius: 2px 0 0 2px !important;
box-shadow: inset 0 1px 0 0 rgba(0,0,0,.2), inset 0 -1px 0 0 rgba(0,0,0,.2), inset 1px 0 0 0 rgba(0,0,0,.2) !important;
}

.db_progress-remainder {
background-color: rgba(50,50,50,.2) !important;
border-radius: 1px 2px 2px 1px !important;
box-shadow: inset 2px 0px 2px -2px rgba(0,0,0,.5) !important;
}
.db_progress-bar[minwidth="255"] + .db_progress-remainder {
border-radius: 2px !important;
}

.db_filenameLabel {
font-size: inherit !important;
margin: 0 4px !important;
padding: 0 !important;
color: inherit !important;
}

.db_finishedHbox .db_filenameLabel {
margin-left: 2px !important;
}

.db_finishedIcon {
margin: 0 0 0 4px !important;
padding: 0 !important;
height: 16px !important;
max-width: 16px !important;
}

