css:
@-moz-document domain("4chan.org"){

html body div.postarea form table tbody tr td.postblock {

border: none !important;

-moz-border-radius: 4px !important;
}

input[type="checkbox"] 

{-moz-appearance: none !important; 

background: rgba(0,0,0,.05) !important; 

border: 1px solid rgba(0,0,0,.1) !important;

height: 12px !important;

width: 13px !important;

-moz-border-radius: 4px !important;}

input[type="checkbox"]:checked

{background: rgba(0,0,0,.2) !important;}

.box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog 

{ background: -moz-linear-gradient( rgba(0,0,0,.06),rgba(0,0,0,.0)) !important;

border: none !important;

-moz-border-radius: 4px !important;

position:relative !important;top:40px; }

a 

{-moz-transition: all 1s ease-in-out 0s;} 

.boxbar, #preview-tooltip-nws H2, #preview-tooltip-ws H2, #disclaimer-dialog H2 

{ background: none !important;

letter-spacing: 2px !important;

text-align: center !important;

text-transform: lowercase !important;

border-bottom: 1px solid rgba(0,0,0,0) !important; }

.column

{ text-align: left !important;

text-transform: lowercase !important; }

.column > H3, .p_postername

{
letter-spacing: 0px !important;}

.boxcontent 

{

letter-spacing: -0.51px !important;


position:relative !important; 

bottom:21px; left:45px !important;  }

#copyright 

{ letter-spacing: 1px !important; position:relative !important;top:100px !important;  }

.boxbar > H2 

{ position: inherit; bottom: 40px;

font-size: 40px !important;

text-transform: lowercase !important; }

.postername, .commentpostername

{ 


letter-spacing: -0.1px !important;

background: transparent !important; }

div.postarea

{position:fixed !important;width:450px !important;height:0px !important;opacity:0.0 !important;right:75px  !important;bottom:550px; -moz-transition: all 0.5s ease-in-out 0s;}

div.postarea:hover

{opacity:1.0 !important; -moz-transition: all 0.2s ease-in-out 0s;}

td.postblock

{vertical-align:top !important;}

.postertrip

{ 


letter-spacing: 0.4px !important;

background: transparent !important; }

.filetitle, .replytitle

{

background: none !important; }

.reply, .replyhl

{ padding: 0px 22px 0px 3px !important; }

form > blockquote

{ counter-reset: replycount 1 !important; }

form > blockquote:only-of-type ~ table .doubledash::after

{ 


counter-increment: replycount !important;

content: counter(replycount) !important;

pointer-events: none !important; }

.reply, .replyhl

{

border: none !important;

-moz-border-radius: 4px !important; }

TD[style="border: 1px dashed;"], TABLE[style="border: 1px dashed black;"] 

{ border: none !important;

background: rgba(136,136,204,.2) !important;

-moz-border-radius: 5px 15px 25px 15px / 10px 30px 40px 31px !important;

-moz-box-shadow: none !important;
}

blink 

{ text-decoration: none !important; }

.logo > FONT:first-of-type SPAN 

{font-size: 40px !important;

text-transform: lowercase !important;}

.logo > FONT:nth-of-type(2)

{ position: relative !important;

top: 0px !important;

left: 0px !important;


text-transform: uppercase !important; }

blockquote a.quotejs:hover, a:hover

{-moz-transition: all 0.1s ease-in-out 0s; }

form hr

{display:block !important;}

hr

{height:1px !important;border:none !important;background:#999 !important;}

table,td

{border:none !important;}

img[md5] 

{height: auto !important; width: auto !important;}

input,textarea

{margin-right:1px !important;}

textarea

{height:150px !important;}

td.postblock

{vertical-align:top !important;}

#navtop 

{ -moz-border-radius: 4px !important;

color: transparent !important;

font:0px calibri !important;

text-align: center !important;

background: rgba(50,50,220,0.06) !important;

width: -moz-available !important;}

#navtop a{ ;

margin: 4px !important;

font-size: 16px !important;}

#backwash_tooltip

{ border: none !important;}

FORM HR

{ visibility: hidden !important; }

.pages 

{text-align: center !important;}

.pages

{ -moz-border-radius: 4px !important;

width: -moz-available !important;}

.pages TD

{ font-size: 0px !important; }

.pages a:not([name="matches"])

{font-size: 14px ;

padding: 1px 10px !important;}

.pages[align="left"] > tbody > tr > td:first-of-type, .pages[align="left"] > tbody > tr > td:last-of-type,

html body div#doc div#bd div#boards.box-outer div.box-inner div.boxcontent div.column h3 span.warning

,#filter-button, #option-button, html body span[style="left: 5px; position: absolute;"],

#ft > UL > LI, #doc > #bd > .yui-g, BODY > TABLE TH, html body form div#footer span#navbotr,

#navbot,html > body > span > a,html body.masked div#disclaimer-dialog_c.yui-panel-container div.underlay,

html body.masked div#disclaimer-dialog_c.yui-panel-container div#disclaimer-dialog.yui-module div.hd,

html body.masked div#disclaimer-dialog_c.yui-panel-container div#disclaimer-dialog.yui-module div.container-close,

html body div#doc div#bd div#announce.box-outer, #ad, iframe, img[src*="http://static.4chan.org/support/"],

img[src*="http://affiliates.jlist.com/click/583?url=http://www.jlist.com/index.html"], td.rules, hr, #navtopr, .logo img, html body div#header span#navtop a [title="Random"],[href="http://boards.4chan.org/b/"],[title="Social"],[href="http://boards.4chan.org/soc/"]

{ display:none !important; }

html body div#doc div#bd div#boards.box-outer div.box-inner div.boxcontent div.column {position: inherit; top: 40px;}

html body.masked div#disclaimer-dialog_mask.mask

{
background-color:inherit!important;
background:inherit !important;
opacity: 1!important;}
}
