css:
/* AGENT_SHEET */
#searchbar > *, .findbar-textbox, #search-box{
  -moz-appearance: none !important;
  background: rgba(20,20,20,.40) !important;
  -moz-border-radius: 6px !important;
  color: #00bfff !important;
}

#urlbar, #urlbar[focused="false"] {
 -moz-appearance: none !important;
  background: rgba(20,20,20,.40) !important;
  -moz-border-radius: 6px !important;
opacity: .3;
text-align:center; 
    font-family: Times New Roman!important;
margin: -2px -1px -1px !important;
color: #ffffff !important;
}


.urlbar-stack {color: #ffffff !important; text-shadow: #ffffff 1px 1px 2px, #ffffff -1px 1px 2px, #ffffff 1px -1px 2px, #000000 -1px -1px 2px !important;  }


.urlbar-over-link-box {background-color: rgba(255,255,255,1) !important;}
.urlbar-over-link-path-label {color: #00bfff !important;}
.urlbar-over-link-host-label {color: #00bfff !important;}

#searchbar:hover > *, .findbar-textbox:hover {
  background: rgba(20,20,20,.75) !important;
  text-shadow: #ffffff 0px 0px 2px !important;
}

#urlbar:hover, #urlbar[focused="true"] {
background: rgba(20,20,20,.5) !important; 
-moz-box-shadow: 0px 0px 3px 3px #333 !important; 
opacity: 1; 
color: #00bfff !important;}

*[disabled="true"] {opacity: .25 !important;}

.findbar-textbox[status="notfound"], menupopup *, popup *, .ac-title-box *, .pilot-title, .tail-up .checkbox-label-box
{
 color: #ffffff !important;
}


#TabsToolbar, #browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #ybToolbar {
 
 background: transparent !important;
 color: #ffffff !important;
 -moz-appearance: none !important;
border: #000000 !important;
 
}


#status-bar{
 margin-top: 1px !important;
}
.findbar-find-next, .findbar-find-previous, .findbar-highlight{
 color: #ffffff !important;
}

#TabsToolbar[tabsontop="true"] { margin-top: -3px !important; margin-bottom: -6px !important; margin-left: -25px !important; }

#nav-bar { margin-top: 0px !important; margin-bottom: -2px !important;}


/*Inactive Tabs*/
.tabbrowser-tab, .tabs-newtab-button {
  background: rgba(20,20,20,0) !important;
  color: #00bbff !important;
  text-shadow: #000000 1px 1px 2px, #000000 -1px 1px 2px, #000000 1px -1px 2px, #000000 -1px -1px 2px !important;
  font-size: 11px !important;
  font-style: italic !important;
  border: #000000 1px solid !important;
-moz-box-shadow: inset 0px 0px 2em 0px rgba(20,20,20,.5) !important;
  opacity: .75 !important;
  padding-left: 4px !important;
  padding-right: 4px !important;
  -moz-border-radius: 0px 0px 9px 9px !important;
  height: 21px !important;

   border: 1px solid rgba(90,30,0,0.8) !important;
     border-top: none !important;
}
.tabbrowser-tab:not([pinned="true"])
{
 margin-left: 1px !important;
 margin-right: 1px !important;
}


#TabsToolbar[tabsontop="true"] .tabbrowser-tab[pinned="true"]
{
 height: 24px !important;
}


#TabsToolbar[tabsontop="false"] .tabbrowser-tab[pinned="true"]
{
 height: 24px !important;
}


.tabbrowser-tab[pinned="true"] .tab-icon-image
{margin-top: 3px !important;}

.tabbrowser-tab[selected="true"]{
-moz-border-radius: 0px 0px 9px 9px !important;
height: 21px !important;
color: #00bfff !important;
text-shadow: #000000 0px 0px 2px !important;
-moz-box-shadow: inset 0px 0px 8px 0px rgba(0,0,0,1), -1px -1px .5em rgba(0,0,0,1) !important;
opacity: 1 !important;
border: 1px solid rgba(90,30,0,0.8) !important;
border-top: .1px inset #00bfff !important;
}

#appmenu-button {
	background: rgba(0,191,255,0) !important;
	color: #00bfff !important;
	text-shadow: rgba(0,0,0,.75) 1px 1px 2px, rgba(0,0,0,.75) -1px 1px 2px, rgba(0,0,0,.75) 1px -1px 2px, rgba(0,0,0,.75) -1px -1px 2px !important;
	-moz-box-shadow: inset 0px 0px 2em 0px rgba(20,20,20,.5) !important;
	-moz-border-radius: 0px 0px 9px 9px !important;
	opacity: .75 !important;

}
#appmenu-button:hover {
	color: #00bfff !important;
	text-shadow: #ffffff 0px 0px 2px !important;
	-moz-box-shadow: inset 0px 0px 8px 0px rgba(0,0,0,1), -1px -1px .5em rgba(0,0,0,1) !important;
	opacity: 1 !important;
}

.tabbrowser-tab:hover {
	text-shadow: #000000 0px 0px 2px !important;
	opacity: 1 !important;
	-moz-box-shadow: inset 0px 0px 8px 0px rgba(0,0,0,1), -1px -1px .5em rgba(0,0,0,1) !important;
  
}

.tabs-newtab-button:hover, #alltabs-button:hover, #new-tab-button:hover, .scrollbutton-down:hover, .scrollbutton-up:hover{
 text-shadow: #000000 0px 0px 2px !important;
 opacity: 1 !important;
}



/* side bar stuff */


#placesCommands, #editMenuCommands, #placesContext, #bhTooltip, #mainCommandSet, tooltip {
 background: rgba(20,20,20,.75) !important;
 color: #ffffff !important;
text-shadow: #ffffff 0px 0px 2px !important;
 -moz-appearance: none !important;
 -moz-border-radius: 6px !important;
}

menupopup, popup, #identity-popup-container, #identity-popup, popupnotification, .panel-inner-arrowcontent
{
 background: rgba(20,20,20,.55) !important;
 color: #ffffff !important;
text-shadow: #ffffff 0px 0px 2px !important;
 -moz-appearance: none !important;
 -moz-border-radius: 6px !important;
}


.popup-internal-box {
 background:rgba(0,0,0,.5) !important;
  -moz-border-radius: 6px !important;
}

#appmenuPrimaryPane, #appmenuSecondaryPane {
 background: transparent !important;
  -moz-border-radius: 6px !important;
}


.tail-up { 
 -moz-border-image: url("http://a.imageshack.us/img7/2817/notificationtailupdark.png") 26 56 22 18 / 26px 56px 22px 18px round stretch !important;

}


#pilot-notification-text {color: #aaaaaa !important;}




#browser, #sidebar, #PopupAutoCompleteRichResult, #sidebar-header {
 background: transparent !important;
  color: #ffffff !important;
  text-shadow: #000000 1px 1px 2px, #000000 -1px 1px 2px, #000000 1px -1px 2px, #000000 -1px -1px 2px !important;
    -moz-appearance: -moz-win-glass !important;
border: #000000 !important;
}

#bookmarksPanel {
 background: transparent !important;
  color: #ffffff !important;
  text-shadow: #000000 1px 1px 2px, #000000 -1px 1px 2px, #000000 1px -1px 2px, #000000 -1px -1px 2px !important;
    -moz-appearance: -moz-win-media-toolbox !important;
border: #000000 !important;
}

#bookmarks-view {
  background: -moz-radial-gradient(top, rgba(255,255,255,.15), rgba(0,0,0,.75));
 padding-right: 5px !important;
}

.autocomplete-richlistbox {
background: rgba(20,20,20,.75) !important;
}

.tabbrowser-strip:not(:hover) .tab-text,
.tabbrowser-strip:not(:hover) .tabs-newtab-button {
opacity: 0.5 !important;
}
.tabbrowser-tab:not(:hover) .tab-icon-image {
opacity: 0.0 !important;
}


#titlebar-spacer { 
   position: relative !important;
   z-index: -1 !important
}

#main-window #navigator-toolbox[tabsontop="true"] #TabsToolbar {    
     padding-left: 89px !important; 
     margin-right: 115px !important; 
}

#main-window[tabsontop="true"] #toolbar-menubar[autohide="false"] { 
     margin-left: 0 !important; 
     margin-top: 0 !important; 
}

#main-window[tabsontop="true"][sizemode="maximized"] #toolbar-menubar[autohide="false"] { 
     margin-left: 0 !important; 
}

#main-window[tabsontop="true"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar { 
     padding-left: 0 !important;
     margin-right: 0 !important; 
}

#main-window[tabsontop="true"] #toolbar-menubar { 
     margin-left: 82px !important; 
     margin-top: -19px !important; 
}

#main-window[tabsontop="true"][sizemode="maximized"] #toolbar-menubar { 
     margin-top: 3px !important; 
}

#appmenu-button dropmarker { 
    display:none !important;
}

#appmenu-button-container{
     position: fixed !important;
}
/* Toolbar Button Settings */

.toolbarbutton-1 {background: transparent !important;
  box-shadow: none !important;
  border: none !important;}

.toolbarbutton-1 > .toolbarbutton-menubutton-button
{background: transparent !important;
box-shadow: none !important;
border: none !important;}

.toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker
{background: none !important;
border: none !important;
box-shadow: none !important;}

#TabsToolbar .toolbarbutton-1 {
  box-shadow: none !important;
}


.toolbarbutton-1[disabled="true"] .toolbarbutton-icon {
  opacity: .35 !important;
}

.toolbarbutton-1:not([disabled="true"]):hover {
background: -moz-radial-gradient(rgb(160,160,180) 30%, transparent, transparent) !important;
}

/* Close button on hover */
.tab-close-button {visibility:hidden}

.tabbrowser-tab:hover .tab-close-button {visibility:visible}

.ac-comment, .ac-url-text {
color: #d3d3d3 !important; }


/*Highlight input and textarea*/

/* This file is licensed under Creative Commons Attribution License
 * http://creativecommons.org/licenses/by/3.0/
 *
 * Initial Developer:
 * Andreas Jung (sd-daken.deviantart.com)
 *
 * Contributor(s):
 *
 */

@-moz-document url-prefix(http), url-prefix(https), url-prefix(file) {
  input:not([type="submit"]):not([type="reset"]):not([type="button"]):not([type="checkbox"]):not([type="radio"]):not([type="hidden"]):not([type="image"]), 
  textarea {
    -moz-appearance: none !important;
    border: 1px solid #7f9db9;
    -moz-box-sizing: padding-box;
    padding: 2px !important;
    min-height: 20px !important;
	}
  }
  
/*scrollbar: from Firefox Theme Part n9*/
scrollbar scrollbarbutton
{visibility: collapse !important}
scrollbar[orient="vertical"] 
{width:5px!important;opacity:0.01!important}
scrollbar[orient="vertical"]:hover
{width:18px!important;opacity:1!important;padding-right:1px!important}
scrollbar[orient="horizontal"] 
{height:5px!important;opacity:0.01!important}
scrollbar[orient="horizontal"]:hover
{height:18px!important;opacity:1!important;padding-bottom:1px!important}

/*About:blank glass*/

#appcontent,
#content > tabbox > tabpanels {
  background-color: transparent !important;
}

#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="View Source Chart"]) #content browser{ 
opacity: 0 !important;
}

/*appmenu button edited by me, created by god knows who*/
/* Show add-on bar top border */
#addon-bar[collapsed="false"] {
     -moz-appearance: none !important;
     border-top: 1px solid rgb(102,102,102)  !important;
}

#FindToolbar {
     border-top-width: 1px !important;
     -moz-border-top-colors: rgb(102,102,102) !important;
}

#addonbar-closebutton {display:none !important;}


/* Firefox logo app menu button */
#appmenu-button-container {
     position: fixed !important;
     margin-top: 0 !important;
     margin-left: 2px !important;
}

#appmenu-button {
     border: 1px solid rgba(90,30,0,0.8) !important;
     border-top: none !important;
     padding: 1px 5px 1px 5px !important;
     height: 20px !important;
     min-width: 40px !important;
     width: 40px !important;
}

#appmenu-button .button-text { 
     color: transparent !important;
     text-shadow: none !important;
     margin-left: -24.5px !important;
     margin-right: -35px !important;
     background: url("data:image/png;base64,%3D%3D") no-repeat 16px !important;
}

#appmenu-button .button-menu-dropmarker {
     list-style-image: url("data:image/png;base64,%3D") !important;
}

*::-moz-selection 
{

   background: #333333 !important; 
   color: #00bfff !important; 

}
