css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");    

#navigator-toolbox:not([customizing]) #TabsToolbar #new-tab-button,
#TabsToolbar #home-button {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
}

#navigator-toolbox:not([customizing]) #TabsToolbar #new-tab-button,
#TabsToolbar #home-button {
  position: static!important;
  -moz-appearance: none!important;
  background: -moz-linear-gradient(hsla(0,0%,100%,.2), hsla(0,0%,45%,.2) 2px, hsla(0,0%,32%,.2) 80%)!important;
  background-origin: border-box!important;
  background-position: 1px 2px!important;
  background-size: 100% -moz-calc(100% - 2px)!important;
  background-repeat: no-repeat!important;
  color: inherit!important;
  margin: 0!important;
  padding: 0!important;
  border-width: 4px 5px 3px 6px!important;
  border-style: solid!important;
  -moz-border-image: url("data:image/png;base64,") 4 5 3 6 / 4px 5px 3px 6px !important;
  border-radius: 10px 8px 0 0!important;
  width: 31px !important;
}

#TabsToolbar #new-tab-button:hover, #TabsToolbar #home-button:hover {
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.6), hsla(0,0%,100%,.2) 4px, hsla(0,0%,75%,.2) 80%)!important;
}

#navigator-toolbox:not([customizing]) #TabsToolbar #new-tab-button:-moz-lwtheme-brighttext, #TabsToolbar #home-button:-moz-lwtheme-brighttext {
  background-image: -moz-linear-gradient(hsla(0,0%,60%,.6), hsla(0,0%,40%,.6) 4px, hsla(0,0%,30%,.6) 80%)!important;
}

#TabsToolbar #new-tab-button:-moz-lwtheme-brighttext:hover, #TabsToolbar #home-button:-moz-lwtheme-brighttext:hover {
  background-image: -moz-linear-gradient(hsla(0,0%,80%,.6), hsla(0,0%,60%,.6) 4px, hsla(0,0%,45%,.6) 80%)!important;
}

#navigator-toolbox:not([customizing]) #TabsToolbar #new-tab-button:-moz-lwtheme-darktext, #TabsToolbar #home-button:-moz-lwtheme-darktext {
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.5), hsla(0,0%,60%,.5) 4px, hsla(0,0%,45%,.5) 80%)!important;
}

#TabsToolbar #new-tab-button:-moz-lwtheme-darktext:hover, #TabsToolbar #home-button:-moz-lwtheme-darktext:hover {
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.5), hsla(0,0%,80%,.5) 4px, hsla(0,0%,60%,.5) 80%)!important;
}

#navigator-toolbox:not([customizing]) #main-window[sizemode="maximized"][chromemargin]:not([inFullscreen]) #TabsToolbar #new-tab-button, #main-window[sizemode="maximized"][chromemargin]:not([inFullscreen]) #TabsToolbar #home-button {  
  background-image: -moz-linear-gradient(hsla(0,0%,80%,.6), hsla(0,0%,75%,.4) 50%, hsla(0,0%,70%,.4) 51%) !important;
  text-shadow: white 0 0 1px;
}

#main-window[sizemode="maximized"][chromemargin]:not([inFullscreen]) #TabsToolbar #new-tab-button:hover, #main-window[sizemode="maximized"][chromemargin]:not([inFullscreen]) #TabsToolbar #home-button:hover {  
  background-image: -moz-linear-gradient(hsla(0,0%,100%,.8), hsla(0,0%,90%,.6) 50%, hsla(0,0%,85%,.6) 51%) !important;
}

@media all and (-moz-touch-enabled) {
  #TabsToolbar #new-tab-button, #TabsToolbar #home-button {
    min-width: 1cm !important;
  }
}

#TabsToolbar #new-tab-button > .toolbarbutton-icon, #TabsToolbar #home-button > .toolbarbutton-icon {
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}
