css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alfaclub.pl")
{
 body { background-color: #000011 !important; }
 .postbody { color: #ffffff !important; line-height:20px !important;  }
 .postdetails { color: #999999 !important; }
 .quote, .code { background-color: #666666 !important; color: #cccccc !important; border:0 none !important; padding:5px !important; }
 .bodyline { background-color: #ff9933 !important; }
 .forumline { background-color: #445566 !important; }
 hr { border: solid transparent 0px !important; border-top-width: 1px !important;  }
 a:link { color : #ddcc00 !important; }
 a:active { color : #ddcc00 !important; }
 a:visited { color : #ddcc00 !important; }
 td.row1 { background-color: #112233 !important; }
 td.row2 { background-color: #001122 !important; }
 td.row3 { background-color: #112233 !important; }
 .cattitle { color: #def !important; }
 td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom,.rowpic { background-image: none !important; background-color:#445566 !important; padding:5px !important}
 td.row3Right { background-color: #112233 !important; }
 td.spaceRow { background-color: #334455 !important; display:none !important; }
 th { background-color: #778899  !important; color: #ffffff  !important; background-image: none !important; }
 input.post, textarea.post, select, .liteoption { background-color: #ffffff !important; color:#000000 !important;}
.button { background-color : #CC0000 !important;  }
 .mainoption { background-color : #ffdd00 !important; color : #000000 !important; }
}
