css:
@-moz-document url-prefix('http://www.reddit.com/') {

body div.side { visibility: hidden; }
body div.content {
 width: 90%;
 background: #f6f6ef; 
 margin-left: auto;
 margin-right: auto;
 font-family: "Helvetica";
}

body div#header, body div#header div#header-bottom-right, body div#header div#header-bottom-left ul.tabmenu li a {
background: #666666;
}

body div#header {
background: -webkit-gradient(linear, left top, left bottom, from(#444444), to(#666666));
}
body div#header div#header-bottom-left ul.tabmenu li.selected a {
 border: dotted;
 border-width: 1px;
 border-color: white;
}

div#header {
 border-bottom: 4px solid #8ca6c0;
}

body div#header a {
 color: #eeeeee;
}

body div#header div#sr-header-area a {
 color: #000000;
}

body div.content div.sitetable div.thing div.entry p.title a.title { 
 color: #111111;
 font-size: 11pt;
 font-family: "Helvetica";
}

body div.content div.sitetable div.thing div.entry p.title a.title:visited {
color: #888888;
}


body div.content div.sitetable div.thing div.entry p.tagline a {
 color: #888888;
} 

body div.content div.sitetable div.thing div.entry p.tagline a.subreddit {
 font-weight:normal;
} 

body div.content div.sitetable div.thing div.entry ul.flat-list li a {
 font-weight:normal;
}

body div.content div.sitetable div.thing {
 padding-top: 10px;
 margin-bottom: 0px;
}

}
