css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(sys.4chan.org) {
body {background: "#333" !important; }
}

@-moz-document domain("boards.4chan.org") {
/* GLOBAL */

* {
  font-family: "Segoe UI", Ubuntu, sans-serif !important;
  font-size: 11px !important;
  font-weight: normal !important;
  margin: 0px !important;
  padding: 0px !important;
}
body, html {
  margin: 0px !important;
  padding: 0px !important;
}
body {
  margin-top: 20px !important;
  color: #999 !important;
  /* bgscroll is either "fixed" or "scroll" */
  background: #000000 url(http://i.imgur.com/DRaZf.jpg) scroll !important;
}
a {
  -moz-transition: all 0.5s ease;
  color: #bbb !important;
}



/* HIDE SHIT */
img.niaxiphoqzqhqccfbzbr,
div.logo img,
div.logo font[size="1"],
hr,
.rules,
.postarea small,
#recaptcha_logo,
#recaptcha_tagline,
th[bgcolor="#e04000"],
.logo,
table[align="right"],
#footer,
span[style="left: 5px; position: absolute;"] {
  display: none !important;
}
input,
textarea,
button,
select {
  font-size: 10px !important;
  background: #1a1a1a !important;
  color: #aaa !important;
  padding: 4px !important;
  border:  1px solid #333 !important;
  -moz-transition: all 0.5s ease;
  -moz-appearance: none !important;
}
input:hover,
textarea:hover,
button:hover,
select:hover {
  background: #1b1c1f !important;
  color: #bbb !important;
  border-color:  #353637 !important;
}
input:focus,
textarea:focus,
button:focus,
select:focus {
  background: #1f2123 !important;
  color: #ddd !important;
  border-color:  #40464c !important;
}
/* TYPOGRAPHY */
/*
.logo b span {
  font-size: 11px !important;
  text-transform: uppercase !important;
  font-weight: normal !important;
  letter-spacing: 0.15em !important;
  padding-left: 10px !important;
  position: fixed !important;
  color: #999 !important;
} */
#navtop:not(a),
#navtopr:not(a) {
  font-size: 0px !important;
}
.logo b span, #header a, #navtop a, #navtopr a {
  text-shadow: 0px 0px 3px #000, 0px 0px 5px #000 !important;
}
#navtop a, #navtopr a {
  font-weight: bold !important;
  font-size: 10px !important;
  margin: 0px 2px !important;
  color: #aaa !important;
}
#navtopr a {
  font-size: 10px !important;
  text-transform: lowercase !important;
}
form[name="delform"] div:not(.thread),
form[name="delform"] div:not(.thread) select,
form[name="delform"] div:not(.thread) option {
  font-size: 10px !important;
  color: #bbb !important;
  text-transform: lowercase !important;
}
form[name="delform"] div:not(.thread) select {
  padding: 2px !important;
  text-align: center !important;
  border: 1px solid #AAAAAA !important;
}

form[name="delform"] div:not(.thread) label {
  font-size: 9px !important;
  text-transform: uppercase !important;
  color: #aaa !important;
}
a.pointer {
  font-weight: bold !important;
  font-weight: normal !important;
  font-size: 9px !important;
  color: #777 !important;
  padding-right: 5px !important;
}
.reply,
.reply a,
td.replyhl,
td.replyhl a,
span.postername,
span.commentpostername,
span.filesize,
span.filesize a,
.postertrip,
span.filetitle,
span.replytitle {
  color: #888 !important;
  font-size: 10px !important;
}
blockquote {
  color: #bbb !important;
  text-transform: none !important;
  font-size: 11px !important;
  margin-bottom: 4px !important;
  background: #343838 !important;
}
span.commentpostername,
span.postername,
span.postertrip {
  color: #80accE !important;
  text-shadow: 0px 0px 6px rgba(20,20,20,0.9);
  font-weight: bold !important;
  background: #343838 !important;
  border: 1px solid #343838 !important;
  border-radius: 5px 5px 0px 0px;
  -moz-border-radius: 5px 5px 0px 0px;
  display: block;
  float: left;
  margin: 0px 10px 0px 0px !important;
  padding: 4px 6px !important;
  padding-top: 2px !important;
}
span.commentpostername,
span.postername {
  /*margin-right: 0px !important;
  padding: 2px 6px !important;
  padding-top: 4px !important;*/
}
span.commentpostername:empty,
span.postername:empty {
  display: none;
}
span.commentpostername a:empty::after,
span.postername a:empty::after {
  content: ">>";
}
span.commentpostername a:empty,
span.postername a:empty {
  text-decoration: none !important;
  color: #444 !important;
  font-size: 8px !important;
  text-shadow: none !important;
  display: block;
  position: absolute;
  padding-right: 6px !important;
  margin-left: -7px !important;
  margin-top: -3px !important;
  background: #343838 !important;
  padding-left: 8px !important;
  padding-top: 4px !important;
  padding-bottom: 8px !important;
  border-radius: 5px 0px 0px 0px;
  -moz-border-radius: 5px 0px 0px 0px;
}
span.commentpostername a:empty:hover,
span.postername a:empty:hover {
  color: #666 !important;
}
span.postertrip {
  color: #608caE !important;
  /*margin: 0px !important;*/
}
span.commentpostername a,
span.postername a {
  color: #95C3E7 !important; /* #608caE  */
  font-weight: bold !important;
  display: block;
/*  padding-bottom: 2px !important; */
}
/* SAGERU INDICATORS~ */
span.commentpostername a[href="mailto:sage"],
span.postername a[href="mailto:sage"] {
  color: #65717A !important;
  text-decoration: none !important;
}
span.commentpostername a[href="mailto:sage"]::before,
span.postername a[href="mailto:sage"] ::before{
  color: #50565B !important;
  content: 'sage ';
}
span.filetitle, span.replytitle {
  font-size: 11px !important;
  text-shadow: 0px 0px 6px rgba(20,20,20,0.9);
  color: #ccc !important;
  padding-left: 5px !important;
  font-weight: bold !important;
}
form[name="delform"] table span.filesize {
  display: block;
  float: right;
  clear: both;
  padding: 4px !important;
  padding-bottom: 0px !important;
  padding-top: 0px !important;
  margin-right: 0px !important;
}
.pages td,
.pages td a {
  font-size: 9px !important;
  text-transform: uppercase !important;
  color: #aaa !important;
}
.unkfunc {
  color: #98b962 !important;
}
.omittedposts {
  display: block !important;
  margin-top: 20px !important;
}
a:hover {
  color: #00DFFC !important;
}


/* HEADER */
#header {
  position: fixed !important;
  background: rgba(25, 25, 25, 0.9) !important;
  top: 0px !important;
  left: 0px !important;
  right: 0px !important;
  padding: 5px 0px !important;
  box-shadow:0px 4px 5px rgba(25, 25, 25, 0.6) !important;
  z-index: 999 !important;
}
#navtop {
  float: right !important;
}
#navtopr {
  float: right !important;
  margin-right: 5px !important;
}
/*
.logo {
  float: left !important;
  text-align: left !important;
  margin-top: -45px !important;
  position: absolute !important;
  width: auto !important;
  z-index: 999 !important;
} */
.postarea {
  text-align: right !important;
  position: fixed !important;
  right: 10px !important;
}
.postarea form {
  float: right !important;
}
.postblock {
  position: absolute !important;
  background: transparent !important;
  border: 0px !important;
  padding: 0px !important;
  margin-left: 247px !important;
  width: 50px !important;
}
.postblock b {
  display: block !important;
  text-align: right !important;
  float: right !important;
  color: #30363c !important;
  font-size: 8px !important;
  text-transform: uppercase !important;
}
.postarea tr:nth-of-type(3n) .postblock {
  margin-left: 195px !important;
}
.postarea tr:nth-of-type(5) .postblock b {
  display: none !important;
}
form[name="delform"] div:not(.thread)  {
  position: fixed !important;
  float: right !important;
  margin-top: -32px !important;
  margin-left: -300px !important;
  z-index: 999 !important;
}
form[name="delform"] div:not(.thread) select {
  padding: 0px !important;
  border-color: #333 !important;
  float: left !important;
}
form[name="delform"] div:not(.thread) select,
form[name="delform"] div:not(.thread) option,
form[name="delform"] div:not(.thread) label input {
  background: #222 !important;
}
form[name="delform"] div:not(.thread) label {
  margin-top: 3px !important;
  margin-left: 8px !important;
  display: block !important;
  float: left !important;
}
form[name="delform"] div:not(.thread) label input {
  float: left !important;
  display: block !important;
  padding: 0px !important;
  border: 1px #333 solid !important;
  margin-top: -2px !important;
  margin-right: 3px !important;
}
#updater {
  z-index: 1000;
  padding: 8px !important;
  color: #aaa !important;
  background: #222 !important;
  width: auto !important;
  height: auto !important;
  bottom: auto;
  top: auto;
  height: auto;
}



/* LIST OF THREADS IN BOARD INDEX */
form[name="delform"] {
  margin-left: 302px !important;
  padding-right: 10px !important;
  margin-right: 0px !important;
  margin-top: -30px !important;
  padding-top: 30px !important;
  padding-bottom: 50px !important;
  background: rgba(52, 56, 56, 0.75) !important;
  border-radius: 0px 0px 0px 50px;
  -moz-border-radius: 0px 0px 0px 50px;
}
.thread {
  clear: both;
  margin-top: 25px !important;
  padding-top: 20px;
  margin-bottom: 70px !important;
  padding-left: 10px !important;
}
form[name="delform"] a img/*[width="251"]*/ {
  margin-left: -287px !important;
  margin-top: -5px !important;
  position: static !important;
  z-index: 990 !important;
  margin-bottom: 0px !important;
  background: #646868 !important;
  border: 1px solid #343838 !important;
  border-radius: 6px 6px 6px 6px;
  -moz-border-radius: 6px 6px 6px 6px;
  display: block;
  box-shadow:0px 0px 15px rgba(25,25,25,0.7) !important;
}

form[name="delform"] table a img/*[width="251"]*/ {
  margin-left: 0px!important;
  margin-right: 10px!important;
  margin-top: -10px !important;
  position: static !important;
  z-index: 999 !important;
  padding: 0px !important;
  margin-bottom: 10px !important;
  width: auto;
  clear: left !important;
  float: left !important;
}
form[name="delform"] div:not(.thread),
#header,
.pages,
form[name="delform"] table[align="right"] {
  -moz-transition: all 0.5s ease;
  opacity: 0.45;
}
form[name="delform"] div:not(.thread):hover,
#header:hover,
form[name="delform"] table.pages:hover,
form[name="delform"] table[align="right"]:hover {
  opacity: 1;
}
#header {
  background: rgba(25, 25, 25, 0.95) !important;
  opacity: 0.95;
}
#header a {
  -moz-transition: all 0.5s ease;
  opacity: 0.25;
}
#header a:hover {
  color: #80ACCE !important;
}

#header:hover ~ form[name="delform"] div:not(.thread),
#header:hover a {
  opacity: 1;
}
.pages {
  background: rgba(25, 25, 25, 0.85) !important;
  border: 0px !important;
  padding: 0px !important;
  position: fixed !important;
  left: 0px;
  bottom: 0px;
  border-radius: 0px 8px 0px 0px;
  -moz-border-radius: 0px 8px 0px 0px;
  box-shadow:0px 0px 20px rgba(25,25,25,0.6) !important;
}
.pages td {
  color: #666 !important;
  font-size: 9px !important;
}
.pages td b,
.pages td a:hover {
  color: #80ACCE !important;
  font-size: 9px !important;
  font-weight: bold !important;
}
.pages input {
  display: block !important;
  border: 0px !important;
  cursor: pointer;
  background: transparent !important;
  padding: 0px !important;
  font-size: 9px !important;
  text-transform: uppercase;
  float: left;
  padding-top: 2px !important;
  -moz-transition: all 0.5s ease;
}
form[name="delform"] table[align="right"] {
  background: rgba(25, 25, 25, 0.85) !important;
  border: 0px !important;
  padding: 0px !important;
  position: fixed !important;
  right: 0px;
  bottom: 0px;
  border-radius: 8px 0px 0px 0px;
  -moz-border-radius: 8px 0px 0px 0px;
  box-shadow:0px 0px 20px rgba(25,25,25,0.6) !important;
  display: block !important;
}
form[name="delform"] table[align="right"] td {
  font-size: 0px !important;
}
form[name="delform"] table[align="right"] tr:last-child,
form[name="delform"] table[align="right"] td input {
  display: none !important;
}
form[name="delform"] table[align="right"] td input[value="Delete"],
form[name="delform"] table[align="right"] td input[value="Report"] {
  display: block !important;
  border: 0px !important;
  cursor: pointer;
  background: transparent !important;
  padding: 0px !important;
  font-size: 9px !important;
  text-transform: uppercase;
  float: left;
  -moz-transition: all 0.5s ease;
}
form[name="delform"] table[align="right"] td::before {
  content: "Selected Posts:";
  font-size: 9px !important;
  text-transform: uppercase;
  float: left;
  padding-top: 1px !important;
  padding-left: 4px !important;
  color: #666 !important;
}
.pages input:hover,
form[name="delform"] table[align="right"] td input:hover {
  text-decoration: underline !important;
  color: #80ACCE !important;
  -moz-transition: all 0.5s ease;
}
form[name="delform"] div:not(.thread) {
  margin-top: -30px !important;
}
form[name="delform"] div:not(.thread) select {
  border: 0px !important;
}
form[name="delform"] div:not(.thread) select option {
  font-size: 9px !important;
  border: 0px !important;
}
form[name="delform"] div:not(.thread) label {
  margin-top: 2px !important;
  font-size: 0px !important;
}
form[name="delform"] div:not(.thread) label input {
  margin-top: 1px !important;
  width: 9px !important;
  height: 9px !important;
  cursor: pointer;
}
form[name="delform"] div:not(.thread) label::after {
  font-size: 9px !important;
  content: "expand";
  -moz-transition: all 0.5s ease;
}
form[name="delform"] div:not(.thread) label:hover::after {
  text-decoration: underline !important;
  color: #80ACCE !important;
  -moz-transition: all 0.5s ease;
}
.postarea input.inputtext[type="password"] {
  border-radius: 0px 0px 8px 0px;
  -moz-border-radius: 0px 0px 8px 0px;
}
.postarea input.inputtext[name="name"] {
  border-radius: 0px 8px 0px 0px;
  -moz-border-radius: 0px 8px 0px 0px; 
}
.postarea input[name="upfile"] {
  margin-left: -1px !important;
}

/* INDIVIDUAL POSTS */
.reply,
td.replyhl {
  background: transparent !important;
  border: 0px !important;
  padding: 0px !important;
  padding-bottom: 0px !important;
  width: 100% !important;
}
#qr {
  top: auto;
  bottom: auto;
}
#qr.reply {
  width: auto !important;
  height: auto !important;
  z-index: 1000;
  background: rgba(52, 56, 56, 0.9) !important;
}
#qr.reply td.postblock {
  display: none !important;
}
td.replyhl {
  border-radius: 6px;
  -moz-border-radius: 6px;
  border: 6px !important;
  padding: 2px !important;
  background: rgba(42, 46, 46, 0.9) !important;
}

blockquote {
  background: #343838 !important;
  margin-bottom: 10px !important;
  padding: 8px !important;
  box-shadow:0px 0px 20px rgba(25,25,25,0.6) !important;
  border: 1px solid #343838 !important;
  border-bottom: 2px solid #444848 !important;
  border-radius: 0px 6px 6px 6px;
  -moz-border-radius: 0px 6px 6px 6px;
  padding-top: 15px !important;

}
.thread table {
  padding-top: 20px !important;
  margin-bottom: -20px !important;
}

form[name="delform"] table blockquote {
  padding: 5px !important;
  background: #343838 !important;
  margin-top: 0px !important;
  min-height: 20px;
  padding-top: 10px !important;
  clear: none;
}

table a[href^="http://images.4chan.org"] + blockquote {
  margin-top: -12px !important;
}

td.doubledash {
  vertical-align: middle !important;
}


#backwash_tooltip {
  border: 0px !important;
  border-radius: 6px;
  -moz-border-radius: 6px;
  border: 6px !important;
  background: rgba(35, 35, 35, 0.8) !important;
  z-index: 1000;
  -moz-transition: all 0.2s ease;
}
#backwash_tooltip table span.filesize {
  display: none !important;
}
#backwash_tooltip table blockquote {
  margin: 0px !important;
  margin-top: 0px !important;
}
#backwash_tooltip table a[href^="http://images.4chan.org"] + blockquote {
  margin-top: -18px !important;
}
#backwash_tooltip table img {
  margin-top: -12px !important;
  margin-right: 10px !important;
}
#options {
  border: 1px solid #343838 !important;
  border-radius: 6px;
  -moz-border-radius: 6px;
  color: #bbb !important;
  border: 6px !important;
  width: auto !important;
  padding: 6px !important;
  background: rgba(52, 56, 56, 0.9) !important;
  border-bottom: 2px solid #444848 !important;
  bottom: auto;
  top: auto;
  height: auto;
}
form[name="delform"] a img {
  -moz-transition: all 1s ease;
}
form[name="delform"] a img.hide + img,
form[name="delform"] table a img.hide + img {
  position: relative !important;
  margin: 0px !important;
  margin-top: -20px !important;
  float: left !important;
  -moz-transition: all 1s ease;
  border: 0px !important;
  border-radius: 0px !important;
  -moz-border-radius: 0px !important;
  box-shadow: none !important;
  z-index: 990 !important;
  background: transparent !important;
}
form[name="delform"] a img.hide + img {
  margin-left: -285px !important;
  float: none !important;
  clear: both !important;
}
form[name="delform"] table a img.hide + img {
  margin-left: -312px !important;
  float: left !important;
  clear: left !important;
  margin-right: 10px !important;
}
#watcher {
  width: auto !important;
  height: auto !important;
  background: #222222 !important;
  z-index: 1000;
  margin-top: 10px !important;
  padding: 5px !important;
}
blockquote table,
.thread blockquote table {
  float: none !important;
  clear: both !important;
  background: rgba(35, 35, 35, 0.4) !important;
  padding: 0px !important;
  margin: 0px !important;
  border-radius: 6px;
  -moz-border-radius: 6px;
}
blockquote table blockquote {
  margin-bottom: 0px !important;
}
blockquote table td {
  border: 0px none !important;
}
form[name="delform"] blockquote table a img,
blockquote table img,
td.reply td.reply img,
form[name="delform"] td.reply td.reply a img {
  margin-bottom: 0px !important;
}
a.backlink {
  display: block !important;
  float: right !important;
  margin-left: 5px !important;
  border: 1px solid #343838 !important;
  -moz-border-radius: 5px 5px 0px 0px !important;
  border-radius: 5px 5px 0px 0px !important;
  background: #343838 !important;
  padding: 2px 4px 2px !important;
  text-decoration: none !important;
}

a.forwardlink {
  color: #608CAE !important;
  text-shadow: 0 0 6px rgba(96, 140, 174, 0.8) !important;
}
td.reply td.reply,
td.reply td.replyhl,
td.reply tr {
  clear: both !important;
  float: none !important;
  display: block !important;
  width: 100%;
}
td.reply td.reply,
td.reply td.replyhl {
  background: rgba(35, 35, 35, 0.4) !important;
  margin: 0px !important;
  border-radius: 6px;
  padding-top: 1px !important;
  -moz-border-radius: 6px;
}
td.reply td.reply span.commentpostername,
td.reply td.reply span.postertrip,
td.reply td.replyhl span.commentpostername,
td.reply td.replyjl span.postertrip {
  border-radius: 0px 6px 0px 0px;
  -moz-border-radius: 0px 6px 0px 0px;
}
td.reply td.reply blockquote,
td.reply td.replyhl blockquote {
  margin-bottom: 0px !important;
  border-bottom: none !important;
  border-radius: 0px;
}
body > table ~ form[name="delform"] table td.reply tr ~ a img,
td.reply tr ~ a img,
body > table ~ form[name="delform"] table td.replyhl tr ~ a img,
td.replyhl tr ~ a img{
  margin-left: 10px !important;
  margin-bottom: 0px !important;
}
body > table ~ form[name="delform"] table td.reply tr ~ blockquote,
td.reply tr ~ blockquote{
  border-radius: 0px 0px 6px 6px;
  -moz-border-radius: 0px 0px 6px 6px;
}
body > table ~ form[name="delform"] table td.reply tr ~ a ~ blockquote,
td.reply tr ~ a ~ blockquote{
  padding-top: 15px !important;
  margin-top: -28px !important;
}
td.reply td.reply a[href^="http://images.4chan.org"] + blockquote,
body > table ~ form[name="delform"] table td.reply td.reply a[href^="http://images.4chan.org"] + blockquote,
td.reply td.replyhl a[href^="http://images.4chan.org"] + blockquote,
body > table ~ form[name="delform"] table td.reply td.replyhl a[href^="http://images.4chan.org"] + blockquote {
 padding-bottom: 30px !important;
}
td.reply td.reply a[href^="http://images.4chan.org"] img,
body > table ~ form[name="delform"] table td.reply td.reply a[href^="http://images.4chan.org"] img,
td.reply td.replyhl a[href^="http://images.4chan.org"] img,
body > table ~ form[name="delform"] table td.reply td.replyhl a[href^="http://images.4chan.org"] img {

  margin-left: 10px !important;
  margin-bottom: 0px !important;
}



/* USER-SET VALUES */
/* scrolling/fixed body background */
body {
  background-attachment: /*[[bgscroll]]*/ !important;
}
/* box shadows */
blockquote,
form[name="delform"] a img,
form[name="delform"] table a img,
backwash_tooltip {
  /* Yes: 0 0 20px rgba(25, 25, 25, 0.6)
   * No: none */
  box-shadow: /*[[boxshadow]]*/ !important;
}
/* text shadows */
span.commentpostername, span.postername, span.postertrip {
  /* Yes: 0 0 6px rgba(20, 20, 20, 0.9)
   * No: none */
  text-shadow: /*[[textshadow]]*/ !important;
}


  body > table ~ form[name="delform"] {
    margin-left: 300px !important;
    padding-left: 10px !important;
    min-height: 100% !important;
  }
  body > table ~ form[name="delform"] div:not(.thread) {
    margin-top: -32px !important;
    margin-left: -310px !important;
  }
  body > table ~ form[name="delform"] a img {
    position: fixed !important;
    margin-top: -10px !important;
  }
  body > table ~ form[name="delform"] table a img {
    position: static !important;
  }
  body > table ~ form[name="delform"] span.filetitle {
    display: block !important;
    position: fixed !important;
    left: 17px !important;
    top: 310px !important;
    width: 250px !important;
    font-size: 12px !important;
    font-weight: bold !important;
    letter-spacing: 3px !important;
    background: transparent !important;
  }
  body > table ~ form[name="delform"] table a[href^="http://images.4chan.org"] + blockquote {
    margin-top: -12px !important;
  }
}


@-moz-document domain("4chan.org") {

/* OPTIONS */
/* UNCOMMENT BEFORE CHANGING OPTIONS, THEN --
  IF FOR ANY REASON THESE OPTIONS ARE NOT WORKING FOR YOU, ADD
    !important TO THE END OF THE OPTION, BEFORE THE SEMICOLON (;) */
  .postarea input[type=text],
  .postarea input[type=password],
  .postarea input[type=file],
  .postarea textarea.inputtext,
  textarea#message,
  .postarea input[type=submit],
  form[name="delform"] div:not(.thread) select,
  form[name="delform"] div:not(.thread) input
   {
    -moz-appearance: none;
  }

/* IF FOR ANY REASON THESE OPTIONS ARE NOT WORKING FOR YOU, ADD
    !important TO THE END OF THE OPTION, BEFORE THE SEMICOLON (;) */
/* END OF OPTIONS */

/* Style Compatibility Hack (overrides styles which make the postarea invisibible) */
  html>body>div.postarea {
    opacity: 1 !important;
  }

/* Post Form Cleanup */
  .postarea .rules,
  .postarea small,
  .postarea td[style="padding: 4px 7px 12px;"],
  .postarea td[style="padding: 4px 7px 12px 7px;"],
  .postarea td[style="padding: 10px 7px 7px 7px;"],
  .postarea td[style="padding: 18px 7px 18px 7px;"],
  .postarea td[style="padding: 10px 7px 7px;"],
  .postarea td[style="padding: 18px 7px;"],
  .postarea #recaptcha_logo,
  .postarea #recaptcha_tagline/*,
  .postarea .postblock*/ {
    display: none !important;
  }
/* Post Form */
  .postarea form:not(#newform) {
    max-width: 307px !important;
    min-width: 307px !important;
    padding-left: 5px;
  }

  .postarea form:first-child {
    position: fixed;
    top: auto;
    left: -4px;
    right: auto;
    bottom: 40px;
    z-index: 995;
    margin: 0 !important;
    -webkit-transition: all .3s ease-in-out 3s;
    -moz-transition: all .3s ease-in-out 3s;
    -o-transition: all .3s ease-in-out 3s;
    opacity: .25;
  }

  .postarea form:first-child .recaptchatable img {
    -webkit-transition: all .3s ease-in-out 3s;
    -moz-transition: all .3s ease-in-out 3s;
    -o-transition: all .3s ease-in-out 3s;
    opacity: .1;
  }
  .postarea form:first-child:hover {
    -webkit-transition: all .3s linear;
    -moz-transition: all .3s linear;
    -o-transition: all .3s linear;
    opacity: 1;
  }
  .postarea form:first-child:hover .recaptchatable img {
    -webkit-transition: all .3s linear;
    -moz-transition: all .3s linear;
    -o-transition: all .3s linear;
    opacity: .5;
  }

  #com_submit,
  .postarea input[type=file] > input[type=button],
  .postarea input[name="upfile"] {
    /* font: 200 7pt "Verdana" !important; */
  }

  .postarea input[type=text],
  .postarea input[type=password],
  .postarea input[type=file][name="upfile"] > input[type=text],
  .postarea input[type=file] > input[type=button],
  .postarea textarea.inputtext  {
    -moz-appearance: none;
    -webkit-appearance: none;
    border-color: rgba(52, 56, 56, 0.75) !important;
    border-width: 1px !important;
    border-style: solid !important;
    width: 302px !important;
    height: 19px !important;
    padding: 1px 0 1px 0 !important;
    z-index: 1;
  }
  body .postarea input[type=file] > input[type=button],
  body .postarea input[type=submit] {
    color: #50565C !important;
    text-transform: uppercase !important;
    font-size: 9px !important;
  }

  .postarea input[type=file] {
    z-index: 1;
  }

  body .postarea input[name="sub"],
  .postarea input[type=file][name="upfile"] > input[type=text] {
    width: 251px !important;
    margin: 0 1px 0 0 !important;
    vertical-align: middle;
  }

  .postarea #com_submit,
  .postarea input[type=file] > input[type=button] {
    width: 50px !important;
    height: 19px !important;
    margin: 0 0 0 0 !important;
    padding: 0 !important;
    vertical-align: middle;
  }

.postarea input[type="file"],
#quickrep input[type="file"] {
-moz-appearance: none;
background: transparent;
border-color: #aaa;
border-width: 1px !important;
border-style: solid !important;
height: 20px !important;
padding: 0 !important;
margin-top: -1px !important;
}

  .postarea textarea.inputtext,
  textarea#message {
    height: auto !important;
    font-size: 10pt !important;
  }

  .postarea textarea#message {
    width: 302px !important
  }

  .postarea input[type=submit] {
    -moz-appearance: none;
    border-color: #aaa;
    border-width: 1px !important;
    border-style: solid !important;
  }

  .postarea td, .reply td {
    z-index: 0;
    padding: 0 !important;
  }
/* Spoiler Image Checkbox */
  html>body>.postarea>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3) {
    font-size: 0 !important;
  }

  html>body>.postarea>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3)>label {
    position: fixed;
    font-size: 10pt !important;
    top: auto;
    left: auto;
    bottom: 63px;
  }

  html>body>.postarea:hover>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3)>label,
  div.postarea:hover #autonoko {
    right: 10px;
    -webkit-transition: right .3s linear;
    -moz-transition: right .3s linear;
    -o-transition: right .3s linear;
  }

  html>body>.postarea:not(:hover)>form>table>tbody>tr:nth-of-type(3)>td:nth-of-type(3)>label,
  div.postarea:not(:hover) #autonoko {
    right: -240px;
    -webkit-transition: right .3s ease-in-out 3s;
    -moz-transition: right .3s ease-in-out 3s;
    -o-transition: right .3s ease-in-out 3s;
  }
/* Captcha */
  .postarea td.recaptcha_image_cell,
  td[style="padding-left: 7px;"] {
    padding: 0 !important;
  }


  .postarea .recaptcha_image_cell>center>#recaptcha_image {
    border-color: rgba(52, 56, 56, 0.75) !important;
    border-width: 1px !important;
    border-style: solid !important;
    background: #1A1A1A !important;
  }


  .postarea:hover .recaptcha_image_cell>center>#recaptcha_image {
    border-color: #aaa !important;
  }

  .postarea .recaptchatable #recaptcha_response_field {
    border-color: #aaa;
    border-width: 1px !important;
    border-style: solid !important;
  }

  .postarea .recaptcha_input_area {
    padding: 2px 0 0 0 !important;
  }

  .postarea tr[height="73"], tr[height="73"] td {
    height: 59px !important;
  }
}
