css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#urlbar {
	color: #505050 !important;}

#urlbar:hover {
	color: #505050 !important;}

#searchbar > */*, #search-box*/ {
    color: #505050 !important;}

#searchbar:hover > *, .findbar-textbox:hover {
	color: #505050 !important;}

#PersonalToolbar{
	 color: #000 !important;}

.tabbrowser-tab:not([pinned="true"]){
    color: #000 !important;
    text-shadow: none !important;}

.tabbrowser-tab[selected]:not([pinned])[fadein] {
    color: #E0FAE9 !important;
    text-shadow: #000000 2px 1px 1px !important;
    box-shadow: 0px -2px 0px #e9e9e9, -1px 0px 2px #404040, 1px -1px 5px -1px #000000,inset -1px 0px 0px 0px #e7e7e7, inset 0px 2px 1px 0px transparent,inset 1px 0px 0px 0px #e7e7e7 !important;
    border-left: 1px solid rgba(224,224,224,0.3) !important;
    border-right: 1px solid rgba(224,224,224,0.3) !important;
    border-top: 0px solid rgba(255,255,255,0) !important;}

.tabbrowser-tab:hover:not([pinned]){
    color: #000 !important;
    text-shadow: #e0e0e0 1px 1px 1px !important;
    box-shadow: 0px -1px 0px #fff, 0px 4px 2px #000000 , 0px -4px 2px -3px #b0b0b0 !important;
    border-left: 2px solid rgba(160,160,160,0.2) !important;
    border-right: 2px solid rgba(160,160,160,0.2) !important;
    border-top: 1px solid rgba(240,240,240,0.3) !important;}

#TabsToolbar, #browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar {
    color: #000 !important;}

/*Inverted icons, reversed back to original black icons*/
#main-window :-moz-any(#back-button, #forward-button, #reload-button, #stop-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button:not([status="active"]), #feed-button, #new-tab-button) > .toolbarbutton-icon
	{list-style-image: url("chrome://browser/skin/Toolbar.png") !important; }

#main-window #alltabs-button
	{list-style-image: url("chrome://browser/skin/mainwindow-dropdown-arrow.png") !important; }

#main-window #tabview-button
	{list-style-image: url("chrome://browser/skin/tabview/tabview.png") !important; }

#main-window .tabs-newtab-button,
#main-window #TabsToolbar #new-tab-button > .toolbarbutton-icon
	{list-style-image: url("chrome://browser/skin/tabbrowser/newtab.png") !important; }

#main-window .scrollbutton-up,
#main-window .scrollbutton-down
	{list-style-image: url("chrome://browser/skin/tabbrowser/tab-arrow-left.png") !important; }
