css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

.dashboard
{
  max-width: 240px !important;
  min-width: 240px !important;
}
#page-container 
{
min-width: 980px !important;
}
.your-activity
{
  width: 100% !important;
}
.main-content 
{
  width: 800px !important;
}
.inline-list
{
  text-align: center;
}
.dashboard .wide-trends ul.trends li, .search-dashboard ul.trends li,
div.trends-inner
{
  width: 100% !important;
}
li.user-small-list-item
{
  width: 100% !important;
}
.user-stats li
{
  padding: 0 6px !important;
}
.user-stats-stat
{
  font-size: 9px !important;
}
}
