css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nytimes.com") { /* userstyles */

.articleBody p {color : black !important; }
.dateline, .byline {color : #AE2268 !important; }

}

@-moz-document domain("blogs.nytimes.com") {

.reviewText,
.entry p,
.entry-content p {color : black !important; }

.displayName {color : #006868 !important; }
.location {color : #0000C0 !important; }
.feedback .caption {color : #206020 !important; }

.time,
.date {color : #AE2268 !important; }
.previous, .next {color : #404040 !important; }

}

@-moz-document domain("query.nytimes.com") {

.columnGroup .byline,
.columnGroup .dateline
 {
 color : #AE2268 !important;
 }
.articleBody p {color : black !important; }

}
