#main .ad_entrybox_under,
#main .custom_ads_titleunder2,
#main .ad_entry_super,
#main .ad_main_end,
#side .ad_side_sky,
#side .ad_side_sky_2nd,
iframe,
#entry_detail p.EntryMoreBanner,
#entry_detail .customads_entry_fd_top,
#entry_detail .ad_entry_title_under,
#main .ad_comment_top,
#entry_detail .rakuten_widget,
#entry_detail .facebook_block,
#entry_detail .amazon_ranking,
#info,
#side div.section:nth-child(2),
#main .entrylists .ad_entrylists_end,
.entrylists > div[class="entry"]{
