css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.ft.com/cms/s/'), url-prefix('http://ft.com/cms/s/') {

    #floating-target.clearfix {
        font-family: Georgia,serif;
        font-size: 95%;
        line-height: 140%;
        background-color: #FFF1E0;
    }
    
    .nav-collection.clearfix {
        font-family: Verdana,Arial,sans-serif;
    }

    .fixed #floating-con .nav-collection h3.section {
        background-color: #DDD;
    }

    body {
        margin-left: 8%;
    }

    #right-rail {
        display: none;
    }

    #page-footer.shadow-right.clearfix {
        background-color: #FFF3E5;
        background-image: none;
    }

    #page-header.shadow-right {
        background-color: #FFF3E5;
        background-image: none;
    }


    #fullpage-container.shadow-right {
        zoom: 120%;
    }

    .article {
        width: 130%;
    }

    .bc {
        zoom: 120%;
    }

    .links-box .subscribe-container {
        font-size: 110%;
    }

    span.gen-freestyle-fsmaller {
        font-family: Verdana,Arial,sans-serif;
        font-style: italic;
        line-height: 100%;
    }
}
