css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cleverbot.com") {

body{
background-color:#000!important;
color:#ccc!important;
}
a img{
display:none!important;
}
td[bgcolor]{
background-color:#000!important;
color:inherit!important;
}
/* message line: last typed by user*/
span[style*="rgb(0, 0, 0)"]{
color:#ddd!important;
}
/* message line: last answer by cleverbot*/
span[style*="rgb(8, 99, 187)"]{
color:#9f0!important;
}
/* message line: earlier typed by user*/
span[style*="rgb(102, 102, 102)"]{
color:#aaa!important;
}
/* message line: earlier answer by cleverbot*/
span[style*="rgb(83, 158, 235)"]{
color:#6c0!important;
}
/* message line: even earlier typed by user*/
span[style*="rgb(153, 153, 153)"]{
color:#777!important;
}
/* message line: even earlier answer by cleverbot*/
span[style*="rgb(155, 195, 247)"]{
color:#390!important;
}
input{
-moz-appearance:none !important;
background-color:#000 !important;
border:1px solid #aaa!important;
color:#efefef!important;
padding:0px 2px !important;
-moz-border-radius:7px !important;
}
input[type="text"]{
margin:4px 6px!important;
-moz-box-shadow: 0 0 5px 2px #008DAF;
width:100%!important;
height:2em!important;
}
input[type="button"]{
margin:6px!important;
}
input[type="button"]:hover{
-moz-box-shadow: 0 0 5px 2px #8DAF00;
color:#88AF00!important;
}
iframe{
display:none!important;
}

}
