@namespace url(http://www.w3.org/1999/xhtml);
/*********************************/
/* interfax.ru (little hide ads) */
/*         By ReColManiac        */
/*********************************/
@-moz-document domain("interfax.ru") {
/* hide reklams */
TD > A[href^="/RCL/BAN/"], .partner, .addthis_toolbox, DIV[id^="Market"], iframe, HTML > BODY > CENTER > TABLE:first-child {display: none !important;}CENTER > TABLE + * + TABLE + * + TABLE + TABLE + TABLE + TABLE + TABLE [valign="top"] > [width="740"]:first-child + TD[width="12"] + TD[width="240"] {display: none !important;}DIV[id^="FlRCL"] {display: none !important;}
/* hide superfluous decor on the main page */
CENTER > TABLE + * + TABLE + TABLE + TABLE + * + TABLE + TABLE[width="1244"] TR > TD[width="240"] {display: none !important;}
/* resizing table */
table, tbody {width: 100% !important; border-spacing:5px !important;}TABLE[align="left"] {width: 0% !important; border-spacing:0px !important;}TABLE[cellpadding="12"] {width: 50% !important; border-spacing:0px !important;}TABLE[width="1244"] {width: 100% !important; border-spacing:5px !important;}
TABLE[width="1244"] DIV > DIV > TABLE[cellspacing="0"] {width: 55% !important;}
}
