/* Buienradar (and other) decrapifier */
/* made by: rctgamer3 - v1.1 23-06-11 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("buienradar.nl"), domain("buienradar.be"), domain("niederschlagsradar.de"), domain("vaderradar.se"), domain("meteox.com") {
DIV.pCBB120,DIV.pCHB,TD[align="right"][style="border: 1px solid rgb(170, 170, 170);"],DIV.pCBB300 > #lr + DIV.pCBl300,TD[width="870"] TD[align="left"],DIV#ctl00_ContentPlaceHolder1_pnlAd300homepage,TABLE TD[width="130"][align="left"],TD[style="width: 120px; vertical-align: top; text-align: center;"],TD[width="120"][valign="top"],DIV.pCBB160,TD[style="border: 1px solid #aaaaaa;"] {display: none;}
INS {display: none !important;}
DIV > TABLE[width="512"][cellspacing="0"] {width: 100% !important;}
}

@-moz-document domain("meteox.nl") {
/* lelijke tekstbalk met tijdden weggehaald, staan gewoon in dropdown menuutje in plaatje */
TABLE[width="336"][height="280"] {display: none;}
TD[width="512"][valign="top"] {float: left;}
TD[width="336"][style="text-align: center;"] {display: none !important;}
DIV#Div2 {visibility: hidden;}
}


@-moz-document domain("sat24.nl") {
DIV#header-banner,SPAN#bannerleft {display: none;}
}
