css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* BOOKMARKS TOOLBAR */
#PersonalToolbar:not([customizing]) {
opacity: 0.0 !important;
position: fixed !important;
top: 0px !important;
z-index: 1000 !important;
width: 100% !important;
background: none !important;
-moz-border-image: url("data:image/png;base64,")
15 / 15px repeat !important;
color: #fff !important;
-moz-transition: all 1s ease-in-out .33s !important;
}
#navigator-toolbox:hover #PersonalToolbar,
#navigator-toolbox[customizing] #PersonalToolbar {
opacity: 1.0 !important;
top: 60px !important;
-moz-transition: all .33s ease-in-out !important;
}
#navigator-toolbox[customizing] #PersonalToolbar {
-moz-transition: none !important;
}

/* BOOKMARK ITEMS */
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item {
-moz-border-radius: 3px !important;
margin: 0px 1px !important;
padding: 2px 5px !important;
border: none !important;
font-weight: bold !important;
color: #fff !important;
text-shadow: none !important;
-moz-box-shadow: none !important;
}
/* ITEM TEXT */
#PersonalToolbar:not([customizing]) .bookmark-item > .toolbarbutton-text {
color: #fff !important;
}
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item:hover,
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item:hover:active,
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item[open="true"] {
border: none !important;
background: -moz-linear-gradient(top, #6f81f5, #3f51f2) repeat-x !important;
color: #fff !important;
}
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item:-moz-system-metric(mac-graphite-theme):hover,
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item:-moz-system-metric(mac-graphite-theme):hover:active,
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item:-moz-system-metric(mac-graphite-theme)[open="true"] {
background: -moz-linear-gradient(top, #7f868d, #69717c) repeat-x !important;
}

/* DROPMARKER */
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item > .toolbarbutton-menu-dropmarker {
opacity: 0.5 !important;
list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAFCAYAAACJmvbYAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAVElEQVQIHWP8////DgYGBkcg/gbEMMANZGxgAEraAfEnIAaBnxDq/zMgrQ+SBOFiqOA/IP0HiJOBmAEmyQ3kbAFiEFgAxGBxmCSINgLijUCsDJMEALdjZcSHZ/znAAAAAElFTkSuQmCC")
!important;
-moz-image-region: rect(0px, 7px, 5px, 0px) !important;
}
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item:hover > .toolbarbutton-menu-dropmarker,
#PersonalToolbar:not([customizing]) toolbarbutton.bookmark-item[open="true"] > .toolbarbutton-menu-dropmarker {
opacity: 1.0 !important;
}
/* SEPARATOR */
#PersonalToolbar toolbarseparator {
border-left: solid 1px rgba(255, 255, 255, 0.33) !important;
background: none !important;
}
/* CHEVRON */
toolbarbutton.chevron {
list-style-image: url("data:image/png;base64,")
!important;
}
