css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org")

{

/* ----------------------------------- CSS Reset ----------------------------------- */

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
	}

/* ------------------------------- Shit just got real ------------------------------- */


body {
	width: 940px !important;
	margin: 20px 0 0 20px !important;
	padding: none !important;
	font: normal 11px Arial, Helvetica, Verdana, sans-serif, sans !important;
	}

.bf, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], .logo img {display:none}
.postarea form div {border: none !important;}
div.postarea {padding: 0 !important;}

div.postarea {
	text-align: left !important;
	padding-left: 160px !important;
	margin-bottom: 2px;
	}

form table {
	border-collapse: collapse !important;
	border-spacing: 0px !important;
	padding: 0 !important;
	}

form table tbody tr {
	border-top: 1px solid #ccc !important;
	margin: 2px !important;
	padding: 2px !important;
	}

form table tbody tr td {
	padding: 4px 0 !important;
	}

td.postblock {
	vertical-align:top !important;
	border: none !important;
	background: none !important;
	width: 160px !important;
	padding: none !important;
	color: #000 !important;
	}

td.postblock b {
	font-weight: normal !important;
	}

.inputtext, input[name="recaptcha_response_field"] {
	border: 1px solid #CCC !important;
	margin-right:2px !important;
	background: #fff !important;
	color: #000 !important;
	}

input.inputtext { width: 250px !important; }

textarea {
	min-height: 130px !important;
	min-width: 400px !important;
	}

hr {
	height: 3px !important;
	color: transparent !important;
	border-top: 1px solid #444 !important; 
	border-bottom: 1px solid #444 !important;
	}

hr[width="90%"] { display: none !important; }

form[name="delform"] table { 
	border: none !important;
	background-color: transparent !important; 
	margin: 0 !important;
	padding: 5px 0 !important;
	margin-top: 10px !important;
	width: 860px !important;
	margin-left: 80px !important;
	}

.abbr {
color: #777 !important;
font: oblique 11px Arial, Helvetica, Verdana, sans-serif, sans !important;
}

.omittedposts {
	display: block !important;
	width: 100% !important;
	text-align: right !important;
	}

.doubledash {
	display: none !important;
	}

.reply, .replyhl {
	width: 860px !important;
	color: #666 !important;
	background: transparent !important;
	border: none !important;
	border-top: 2px solid #CCC !important;
	padding: 0 !important;
	font-size: 10px !important;
	}

.replyhl { background-color: #EEE !important; }

blockquote {
	padding: 5px 0 !important;
	margin: 3px 0 !important;
	width: 620px !important;
	color: #000 !important;
	font-size: 12px !important;
	font-family: Georgia !important;
	line-height: 19px !important;
	word-wrap: break-word !important;
	}

form[name="delform"] > blockquote, .thread > blockquote {
	margin-left: 320px !important;
	}

.reply blockquote, .replyhl blockquote {
	margin-left: 240px !important;
	}

img[align="left"] { margin: 5px 0 !important; clear: all !important; }

.quotelink { color: #C30 !important; }

td[align="right"] {
	display: none !important;
	}

.pages td {
	color: transparent !important;
	text-align: center !important;
	}

.pages a {
	font-size: 13px !important;
	padding: 2px 5px !important;
	}

#updater, #options, #qr {
	width: auto !important; 
	background: #eee !important; 
	}

}
