@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cinecommunity.org") {

body {
background:#000 url(http://i.imgur.com/SJ4hE.jpg) fixed top center no-repeat !important;
color:#aaa !important;
margin:0 !important }

a:link, body_alink { color:gold !important }
a:hover, a:active, body_ahover { color:#F50 !important }

table[style="border-top-width:0px"] { border-top:1px solid rgba(255,255,255,0.6) !important }

.page { background-color:rgba(0,0,0,0.6) !important; color:#aaa !important; width:932px !important }

.tfoot { background-color:rgba(255,255,255,0.2) !important }

.tborder { background-color:transparent !important; border-collapse:collapse;
border:none !important }

.vbmenu_control,
.thead, .tcat { background:rgba(255,255,255,0.3) none !important }

td, th, p, li { font:10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif !important }

.thead, 
.alt2, .alt1,
.alt1Active { border-bottom:1px solid rgba(155,155,155,0.1) !important }

.alt2, .alt2Active { background-color:rgba(255,255,255,0.95) !important }
.alt1, .alt1Active { background-color:rgba(255,255,255,0.97) !important }
.alt2, 
.alt2Active,
.alt1, .alt1Active { color:#444 !important }

.alt2 a, .alt1 a,
.alt2Active a,
.alt1Active a { color:#555 !important }

#vB_Editor_001_textarea { width:642px !important }

.vbmenu_popup { background-color:#9B9B9B !important; border-color:#9B9B9B !important }
.vbmenu_popup .thead { background-color:#5B5B5B !important }
.vbmenu_option { background-color:transparent !important }
.vbmenu_hilite,
.vbmenu_option { border-top:1px solid #afafaf !important }
.vbmenu_hilite { background-color:#8B8B8B !important }

.thead[style="font-weight:normal; border: 1px solid #D1D1E1; border-left: 0px"],
.thead[style="font-weight:normal; border: 1px solid #D1D1E1; border-right: 0px"] { border:none !important; border-top: 1px solid rgba(255,255,255,0.6) !important }

td[style="border: 1px solid #D1D1E1; border-top: 0px; border-bottom: 0px"] img { max-height:170px !important; max-width:170px !important }

.time { color:#666 !important }

.thead a,
.tfoot a,
.tcat a,
.thead a:visited,
.vbmenu_popup .thead a,
.vbmenu_option a,
.vbmenu_hilite a,
.vbmenu_control a,
.vbmenu_option { color:#fff !important }

img[src="http://cinecommunity.to/bannercinecom.jpg"],
img[src="http://www.cp-board.to/freak/CH_2.png"] {
background:transparent url(data:image/gif;base64,) no-repeat !important;
height:0 !important;
width:0 !important;
padding-top:23px !important;
padding-left:187px !important }

img[src="images/buttons/newthread.gif"] {
background:transparent url(data:image/gif;base64,) no-repeat !important;
height:0 !important;
width:0 !important;
padding-top:14px !important;
padding-left:103px !important }

img[src="images/buttons/reply.gif"] {
background:transparent url(data:image/gif;base64,R0lGODlhXgAOAIABAP///////yH5BAEAAAEALAAAAABeAA4AAAKwTIBolqwJwWpHVTlpjptzh2mXBZaRRqXkuYwQ6L3l1T7YLIr0roarpUORcENbDVgc8pC+5mb2hBJvlWpnJ3UQo0Ln8RZsdLGvnLIa4/5Y3+h13JWYqVrjGnqik2WjvfzOh5WB9qcGxtTiByg0aDKXSBg5ZZg41saX9xSoE2k0+ekW9reGieg2aHfotURnSekHKggzegcr6/pp2dMGietCtcv7m1sbnIV3FshaSWpKUQAAOw==) no-repeat !important;
