css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
 url("krautchan.net"),
 url-prefix(http://krautchan.net/),
 url-prefix(https://krautchan.net/),
 domain("www.krautchan.net") {

body {
 background: #000 !important; color: #aaa !important; }

h1, form, td, .file_thread, .file_thread a, .postbody, .newsbody, .postsubject, .site_name {
 color: #aaa !important; }

.file_thread {
 background: #111 !important;
}

.menu_title, h2 {
 color: #aaa !important;
}

.menu_title, h2 {
 background: #0d0d1c !important; }

 .postreply, .newsbody, .postreply_highlighted, td.label, span.hidden_thread, .board_newposts, .postreply {
 background: #16161a !important; }

.file_thread, .file_reply {
 background: #27272f !important;
 margin: 5px 10px 20px 5px !important; padding: 5px !important; }

.file_thread {
 border: 1px solid #27272f !important; }

.file_reply {
 border: 1px solid #27272f !important; }

.postreply, .newsbody, table, tr, td, td.label, span.hidden_thread {
 border: none !important; }

.postreply_highlighted {
 border: 1px solid #295166 !important; }

.spoiler {
 color: #000 !important; background: #295166 !important; }

span.spoiler:hover {
 color: #000 !important; }

.authority_admin, .ban_mark, .sage {
color: #990000 !important;
}

.sage:after {
 content:" SÄGE!" !important;
 color: #990000 !important;
 margin-left: -44px !important;
}

span.nav_group_button {
 background: #484848 !important;
 border-color: #aaa !important;
 color: #aaa !important; }

.postheader .postdate, .site_slogan {
 color: #666 !important; }

.postreply .postdate, .postreply_highlighted .postdate, .postheader a.quotelink {
 color: #7c7c80 !important;
}
.postername {
 color: #295166 !important;
}

.postform ul {
 font-size: 70% !important; }

a {
 color: #295166 !important; }

.boardlist a {
 font-size: 90% !important;
 color: #295166 !important;
}

.boardlist a:hover {
 background: #222 !important;
}

/*Reply-Sidebar*/

#postform{
position: fixed !important;
/*top individuell anpassen*/
bottom: 0px !important;
width: 935px !important;
right: -932px !important;
height:100%;
border-left: 2px solid #27272f;
border-top: 2px solid #27272f;
background: url("http://img851.imageshack.us/img851/2452/ohlookheinrichresizedda.png") no-repeat bottom right #16161a !important;

}
#postform:hover{
right: 0px !important;
width: 50%;
} 

