css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('boards.4chan.org') {
body {
 background: #000 !important;
 color: #828282 !important;
}

form { background: #000 !important; }

a {
 color: #aaa !important;
}

hr {
 display: none !important;
}

.logo {
 color: #666 !important;
}

.logo font b span{
 color: #666 !important;
}

.rules{
 display: none !important;
}

td.postblock {
 background: #111 !important;
 border: 1px solid #1a1a1a !important;
 color: #999 !important;
}

td.reply {
background-color:#111 !important;
width: 0% !important;
padding: .2em !important;
border: 0px solid #555 !important;
color: #828282 !important;
-moz-border-radius:0px !important;
}

td.replyhl {
background-color:#222 !important;
width: 0% !important;
padding: 0em !important;
border: 0px solid #4B4B4B !important;
color: #d0d0d0 !important;
-moz-border-radius:0px !important;

}

.omittedposts, .abbr {
 color: #555 !important;
}

.spoiler
  { 
    background: #27272f !important;
    color: transparent !important; 
}
.spoiler[style^="color: rgb(255, 255, 255)"]
  { 
    background: transparent !important;
    color: #999 !important;
}

.quotelink {
 color: #496f90 !important;
}

.unkfunc {
 color: #666f96 !important;
}

.commentpostername, .postername, .quotejs {
color: #999 !important;
}

.postertrip {
 color: #888 !important;
}

.commentpostername a.linkmail {
 color: #496f90 !important;
}

.commentpostername a.linkmail[href="mailto:sage"] {
 color: #777 !important;
 text-decoration:none !important;
}

.commentpostername a.linkmail[href="mailto:sage"]:after {
content:" (sage) ";
 color: #e42217 !important;
 font-weight:normal !important;
}

.filetitle, .replytitle {
 color: #666f96 !important;
}


th {
 background: #111 !important;
 padding: .2em !important;
 font-size: small;
 width: 200px !important;
}

#hd {
display: none;
}

.boxbar {
background: #444 !important;
color: #d0d0d0 !important;
}

.box-outer {
border: none !important;
background: #333 !important;
color: #d0d0d0 !important;
}

.box-inner {
 background: #333 !important;
color: #d0d0d0 !important;
}


#ft {
display: none;
}

.pages {
 background: #000 !important;
 border: none !important;
}

.pages td, .pages td b {
color: #d0d0d0 !important;
}

#recent-images li{
 background: #333 !important;
}
pre, textarea {
   color: inherit !important;
   background: #333333 !important;
   font-size: 10pt ! important;
   border-left-color: gray !important;
   border-right-color: gray !important;
   border-top-color: gray !important;
   border-bottom-color: gray !important;
}
input:not([type="button"]):not([type="checkbox"])
:not([type="submit"]):not([type="reset"]), 
select {
    color: inherit !important;
    background: #333333 !important;
    border-left-color: gray !important;
    border-right-color: gray !important;
    border-top-color: gray !important;
    border-bottom-color: gray !important;
} 
.postarea input[type="file"]{
opacity:1 !important;
}

#header {
width: 100% !important;
position: fixed !important;
top: 0px !important;
left: 0px !important;
z-index: 2 !important;
background: #000 !important;
color: #666 !important;
border: 2px !important;
border-color: #000 !important;
}
#navtopr { display: none !important; }
#navtop { width: 100% !important; text-align: left !important; }
}

@-moz-document 
domain("images.4chan.org"),
domain("static.4chan.org"),
domain("sys.4chan.org"),
domain("4chanarchive.org/images/*") {
body {
 background: #000 !important;
 color: #d0d0d0 !important;
}

.boxcontent {
 background:#000 !important;
}

.boxbar {
 background:#222 !important;
 color:#666f96 !important;

}
}
