css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("momomhf.doorblog.jp") {

/* Non-display part */
#header2,
.footer-outer,
.blog-description-outer,
#sub,
#extra,
.article-footer,
.article-options,
.article-body-more > iframe,
.article-body > ins
{ display: none !important; }

/* Display part */
body,
#container,
.blog-title-outer,
.blog-title-outer-2,
.blog-title-outer-3,
#blog-title,
#content,
#main,
.column,
.column-inner,
.column-inner-2,
.article-outer,
.article-outer-2,
.article-outer-3,
.article-header
{
    width: auto !important;
    background: #ffffff !important;
    color: #000000 !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    padding: 0 0 0 !important;
    margin: 0 0 0 0 !important;
}

#blog-title
{
    background: #000000 !important;
    color: #ffffff !important;
}

.article-header
{
    background: #cccccc !important;
    color: #000000 !important;
}


}
