css:
@-moz-document domain(fm-base.co.uk) {

/*Main*/
html {
background: #121212 url(http://www.forkingorcountry.com/FM-base/bg.gif) repeat !important;
background-attachment: fixed !important;
}

body {
padding: 0 !important;
background-color: #FFF !important;
border-left: 1px solid #111;
border-right: 1px solid #111;
}

.body_wrapper {
background-color: #FFF !important;
}

/*.fm11-header-container {
background: transparent no-repeat !important;
}*/

/*Nav tabs*/
.navtabs li a.navtab, .navtabs {
background-color: #033B59 !important;
}

.navtabs li.selected a.navtab, .navtabs ul.floatcontainer {
background-color: #034569 !important;
}

.navtab a:link {text-decoration:none;}
.navtab a:visited {text-decoration:none;}

.navtab a:hover {
text-decoration: underline;
color: #FFF !important;
}

/*Breadcrumbs*/
.lastnavbit, #breadcrumb a {
color: #000 !important;
}

/*Thread tools*/
.popupmenu a.popupctrl {
background-position: 100% 50% !important;
}

#forumdisplaypopups a.popupctrl {
padding-right: 15px;
}

/*Forum Elements*/
#forums {
border-left: 1px solid #111;
border-right: 1px solid #111;
border-bottom: 1px solid #111;
}

.forumhead {
background-color: #333 !important;
border-top: 1px solid #111 !important;
}

/*Sidebar elements*/
#sidebar_container {
padding: 0 0 5px 0 !important;
}

#sidebar .block.smaller {
border: 1px solid black;
}

/*Footer elements*/
.footer {
box-shadow: 0px 0px 0px !important;
}


/*Text*/
#pagetitle, a.firstunread, .popupctrl {
color: #000 !important;
}

.floatcontainer .popupctrl, .isuser .popupctrl {
color: #FFF !important;
}

}
