css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stumbleupon.com")  {

/* StumbleUpon - Dark Shiny Blue, transparency   */
/* based on http://userstyles.org/styles/11922 and using same background image */

body {
	background: url("http://i6.photobucket.com/albums/y249/uglook/tech3.jpg") #222 fixed repeat left top !important;
	color:#fff !important;
}

body .listStumble .views span, body .colRight .listUrls .views span, body a, body #dashboard a,
.audienceTools #content p.subhead, .audienceTools p, .widgets_landing #content p.subhead, .widgets_landing ul.featureBoxes p {
	color: #6AABD3 !important;
}

/* Backgrounds */
#wrapperContent {
	background: transparent !important;
}
#wrapperHeader, #wrapperFooter {
	background-color: rgba(0,0,0,0.4) !important;
}
#wrapperNav {
	background-color: rgba(6,55,105,0.5) !important;
}
body #navSecondary .filter,
body .filter,
body #navSecondary,
body #listTopics,
body #listCats ul li.current,
body .boxMod1 {
    background-color: rgba(20,61,102,0.5) !important;
    border-color: rgba(20,61,102,0.5) !important;
}
#navChannels,
.wrapperTable,
#topicsTable {
	background-color: rgba(0,0,0,0.2) !important;
	border-color: rgba(0,0,0,0.2) !important;
}
.exploreInterests #keywordForm fieldset,
.colRight fieldset.searchInline,
#searchNav fieldset {
	background-color: rgba(255,255,255,0.5) !important;
	border-color: rgba(255,255,255,0.8) !important;
}
#categories #Selected_Topics span,
#categories .current a, #categories .active a,
#wrapperCategory #topicsList {
    background-color: rgba(255, 255, 255, 0.1) !important;
}
#topicsTable td {
	background: transparent !important;
}
body .hilight,
body .listStumble li.active {
	background-color: rgba(51,82,126,0.5) !important;
}
#navSecondary li.selected a, .activity #navSecondary .activity a, .channels #navSecondary .channels a, .toprated #navSecondary .toprated a,
.shares #navSecondary .shares a, .subscriptions #navSecondary .subscriptions a, .subscribers #navSecondary .subscribers a,
.onlinenow #navSecondary .onlinenow a, .visitors #navSecondary .visitors a, .mayknow #navSecondary .mayknow a, .all #navSecondary .all a,
.reviews #navSecondary .reviews a, .photos #navSecondary .photos a, .videos #navSecondary .videos a, .discoveries #navSecondary .discoveries a,
.list #navSecondary .list a, .grid #navSecondary .grid a, .created #navSecondary .created a, .rated #navSecondary .rated a,
.score #navSecondary .score a, div.settings #navSecondary .settings a, .customize #navSecondary .customize a, .interests #navSecondary .interests a,
.picture #navSecondary .picture a, .common #navSecondary .common a, .all-groups #navSecondary .all-groups a, .popular #navSecondary .popular a,
.suggested #navSecondary .suggested a, .newest #navSecondary .newest a, li.hasChild.hover a, li.active .controls a,
#navSecondary li.selected a, .activity #navSecondary .activity a, .channels #navSecondary .channels a, .toprated #navSecondary .toprated a, .shares #navSecondary .shares a, .subscriptions #navSecondary .subscriptions a, .subscribers #navSecondary .subscribers a, .onlinenow #navSecondary .onlinenow a, .visitors #navSecondary .visitors a, .mayknow #navSecondary .mayknow a, .all #navSecondary .all a, .reviews #navSecondary .reviews a, .photos #navSecondary .photos a, .videos #navSecondary .videos a, .discoveries #navSecondary .discoveries a, .list #navSecondary .list a, .grid #navSecondary .grid a, .created #navSecondary .created a, .rated #navSecondary .rated a, .score #navSecondary .score a, div.settings #navSecondary .settings a, .customize #navSecondary .customize a, .interests #navSecondary .interests a, .picture #navSecondary .picture a, .common #navSecondary .common a, .all-groups #navSecondary .all-groups a, .popular #navSecondary .popular a, .suggested #navSecondary .suggested a, .newest #navSecondary .newest a, li.hasChild.hover a, li.active .controls a, .audienceTools #navSecondary .audienceTools a, .widgets_landing #navSecondary .widgets_landing a, .badges_landing #navSecondary .badges_landing a, .stumblethru #navSecondary .stumblethru a {
    background-color: rgba(255,255,255,0.3) !important;
}
li.hasChild ul {
	background-color: rgb(20,61,102) !important;
}
/* end Backgrounds */

/* Text-shadows */
a, .UIActionButton_Text, span, div, input[value="Comment"] {
	text-shadow: #000 1px 1px 1px !important;
}
.UIComposer_InputArea * {
	text-shadow: none !important;
}
#profile_name {
	text-shadow: #fff 0px 0px 2px, #000 1px 1px 3px;
}
a:hover, input[type="submit"]:not(.fg_action_hide):hover, a span:hover, a:hover *, button:hover {
	color: #99ccff !important;
	text-shadow: #fff 0px 0px 2px !important;
	text-decoration: none !important;
}
/* end text-shadow section */

.colRight .sendMessageTo a:hover,
.emailService li a:hover,
.friends_invite #content fieldset,
.friends_invite dl.colLeft dd,
.listStumble .hilight ul.controls a,
.audienceTools .showcase {
	background-color: rgba(200,220,255,.2) !important;
	background-image: none !important;
}

#navMain .active a,
.homeLoggedIn #navMain .home a,
.discover #navMain .discover a,
.stumblers #navMain .stumblers a,
.favorites #navMain .favorites a {
	color: #AACCFF !important;
	text-shadow: 1px 1px 2px #000000;
	box-shadow: 0 -2px 5px 1px #99CCFF, 0 -2px 5px 0 rgba(0, 0, 0, 0.75) inset !important;
	background: none repeat scroll 0 0 rgba(20, 20, 20, 0.5) !important;
	border-bottom: none !important;
}

#navMain .active a:hover,
.homeLoggedIn #navMain .home a:hover,
.discover #navMain .discover a:hover,
.stumblers #navMain .stumblers a:hover,
.favorites #navMain .favorites a:hover {
	color: #FF6666 !important;
}

.btnGreen, .btnGreen:hover {
	color: #FFFFFF !important;
	text-shadow: none !important;
}

.btnYellow, .btnYellow:hover {
	color: #000 !important;
	text-shadow: none !important;
}

.btnWhite, .btnWhite:hover {
    color: #206BA4 !important;
    text-shadow: none !important;
}

h1, h2, h3, h4, h5, h6 {
	color: #89A8B6 !important;
}

/* widget */
.su-widget-cl-1, 
.su-widget-cl-1 h2 {
	border: 1px solid #AACCFF !important;
}

#su-widgets h2,
#su-widgets .footer {
	background: none repeat scroll 0 0 rgba(51,82,126,0.5) !important;
}


} // END CODE
