@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.gamebanana),
url-prefix(http://gamebanana.com/threads),
url-prefix(http://gamebanana.com/maps)
{

body {
background-color:#2D2D2D !important;
padding:10px !important;
color:#E2E2E2 !important;
font:12px 'verdana' !important
}
div#Header {
opacity:0.9 !important;
height:96px !important;
min-width:1050px !important;
background:#132B38 !important;
border:1px solid #444444 !important;
background:-webkit-gradient(linear,left top,left bottom,from(#222222),to(#1A1A1A)) !important;
background:-moz-linear-gradient(top,#222222,#1A1A1A);
-moz-box-shadow:0 0 25px #000000; -webkit-box-shadow:0 0 25px #000000 !important;
box-shadow:0 0 25px #000000 !important;
-moz-border-radius: 6px !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
}
div#TopBanner {
float:right !important;
width:728px !important;
height:90px !important;
padding:3px 3px 3px 0 !important
}
div.Module div.Content { 
overflow-x:none; 
overflow-y:auto; 
border:1px solid #1F2E35; 
border-top:1px solid black; 
background:#132B38 !important;
-webkit-border-bottom-left-radius: 8px !important;
-webkit-border-bottom-right-radius: 8px !important;
-moz-border-radius-bottomright: 8px !important;
-moz-border-radius-bottomleft: 8px !important;
border-bottom-left-radius: 8px !important;
border-bottom-right-radius: 8px !important;
padding:2px 
}
div.Module { 
margin-bottom:8px; 
-webkit-border-bottom-left-radius: 8px !important;
-webkit-border-bottom-right-radius: 8px !important;
-moz-border-radius-bottomright: 8px !important;
-moz-border-radius-bottomleft: 8px !important;
border-bottom-left-radius: 8px !important;
border-bottom-right-radius: 8px !important;
border:1px solid black 
}
div#Footer { 
opacity:1.0 !important;
background:#132B38 !important;
border:1px solid #444444 !important; 
background:-webkit-gradient(linear,left top,left bottom,from(#222222),to(#1A1A1A)) !important;
background:-moz-linear-gradient(top,#222222,#1A1A1A) !important;
right:10px !important;
-moz-box-shadow:0 0 25px #000000 !important; 
-webkit-box-shadow:0 0 25px #000000 !important; 
box-shadow:0 0 25px #000000 !important;
-moz-border-radius: 6px !important;
-webkit-border-radius: 6px !important;
border-radius: 6px !important;
 }
a:hover {
color:#AAD2EE !important 
}
a:visited:hover {
color:#AAD2EE !important 
}
a img:hover {
outline: 1px solid #AAD2EE !important 
}
input:hover { 
outline:1px solid #AAD2EE !important;
}
input:focus { 
outline:1px solid #FFD800 !important;
}
.UserText { font:14px 'trebuchet ms' !important ;
letter-spacing:0.5px !important 
}
}
