css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
    #masthead {
        height:220px !important;
    }
    .nav {
        position:relative !important;
        left:5px !important;
        width:869px !important;
    }
    .nav-item .content {
        width:202px !important;
    }
    .user-info {
        width:100% !important;
        text-align:center !important;
    }
    .logo img {
        width:309px !important;
        height:134px !important;
        background-image:url(http://img212.imageshack.us/img212/2034/youtubebigns5.png) !important;
    }
    .logo {
        width:100% !important;
        text-align: center !important;
    }
    #hpEmbedTopCap,
    #ad_creative_1,
    #pvaHl,
    #burl,
    #canv,
    #myAd_banner,
    #myAd_pva,
    #ifr_companion {
        visibility:hidden !important;
        height:0px !important;
        display:none !important;
    }
    #myAd_pva div {
        visibility:hidden !important;
        height:0px !important;
        display:none !important;
    }
    #hpVideoList {
        max-height:450px !important;
        overflow:auto !important;
        width:535px !important;
    }
    #footer .search {
        visibility:hidden !important;
        height:0px !important;
        margin:0px !important;
        padding:0px !important;
        border:none !important;
        display:none !important;
    }
    #vidTitle {
        text-align:center !important;
        font-size:18px !important;
    }
    .vEntry:hover,
    .commentEntry:hover,
    #more_channel_videos div:hover {
        background:#EEEEEE !important;
        cursor:pointer !important;
    }
    .search-bar .upload-button {
        visibility:hidden !important;
        display:none !important;
        z-index:-1 !important;
    }
    #search-form {
        display:block;
        width:865px !important;
        height:30px !important;
        padding-top:7px !important;
        padding-left:2px !important;
        padding-right:5px !important;
        margin-right:5px !important;
        text-align:left !important;
    }
    #search-form #search-term {
        width:720px !important;
        float:left !important;
        z-index:0 !important;
    }
    #search-form #search-button {
        display:block !important;
        width:81px !important;
        height:25px !important;
        cursor:pointer !important;
        text-indent:-9999px !important;
        border:none !important;
        background: url(http://img265.imageshack.us/img265/8573/searchbuttonsa1.png) no-repeat left top !important;
        }
}
