css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.bookmark-item > .toolbarbutton-icon {
  width: 14px !important;
  min-height: 14px !important;
  max-height: 14px !important;
  border-top: 10px none !important;
}

toolbarbutton.bookmark-item {
  font-weight: bold;
  color: #333 !important;
  border: 0;
  border-radius: 3px !important;
  padding: 4px 5px !important;
  margin: 0 0  0px !important;
}

#home-button {
  -moz-image-region: rect(0px, 120px, 20px, 100px) !important;
}

#back-button,
toolbar:not([mode="icons"]) #forward-button:-moz-locale-dir(rtl) {
  -moz-image-region: rect(-1px, 40px, 20px, 22px) !important;
}

#forward-button,
toolbar:not([mode="icons"]) #back-button:-moz-locale-dir(rtl) {
  -moz-image-region: rect(-1px, 58px, 20px, 40px)!important;
}

#home-button.bookmark-item {
  list-style-image: url("http://www.blobmanlabs.com/rw_common/themes/falconpro/navicons/Home.png");
  -moz-image-region: rect(0, 12px, 12px, 0) !important;
}

.bookmark-item{
  border: 1px solid transparent !important;
  box-shadow: none !important;
  background: none !important;
  border-radius: 3px !important;
  -moz-transition: none !important;
}

.bookmark-item:not([disabled="true"]):hover{
  background: -moz-linear-gradient(rgba(255,255,255,.5),
                                   rgba(255,255,255,.1)) padding-box !important;
  border: 1px solid rgba(0,0,0,.2) !important;
  box-shadow: 0 1px 0 0 rgba(255,255,255,.3) inset,
              0 1px 0 0 rgba(255,255,255,.3) !important;

}
.bookmark-item[type="menu-button"] toolbarbutton:hover:active,
.bookmark-item[type="menu-button"][open] dropmarker,
.bookmark-item:not([disabled]):not([type="menu-button"]):hover:active,
.bookmark-item:not([disabled]):not([type="menu-button"]):not(:active)[open] {
  border-color: rgba(0,0,0,.3) !important;
  background: rgba(0,0,0,.1) !important;
  box-shadow: 0 1px 0 rgba(0,0,0,.1) inset,
              0 2px 0 rgba(0,0,0,.05) inset,
              1px 0 0 rgba(0,0,0,.05) inset,
              -1px 0 0 0 rgba(0,0,0,.05) inset !important;
}

.bookmark-item[type="menu-button"]>*{
  box-shadow: none !important;
  background: none !important;
  border: none !important;
  -moz-transition: none !important;
}

.bookmark-item:not([disabled])[type="menu-button"]:not(:active)[open]{
  border-color: rgba(0,0,0,.3) !important;
}

.bookmark-item[type="menu-button"] toolbarbutton{
  border-radius: 3px 0 0 3px !important;
}

.bookmark-item[type="menu-button"] dropmarker{
  border-left: 1px solid transparent !important;
  border-radius: 0 3px 3px 0 !important;
}

#nav-bar .bookmark-item[type="menu-button"]:hover dropmarker,
#nav-bar .bookmark-item[type="menu-button"]:active dropmarker,
#nav-bar .bookmark-item[type="menu-button"][open="true"] dropmarker{
  border-left: 1px solid rgba(0,0,0,.25) !important;
}

