css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("endoftheinter.net") {

body
{
	background-image:url(data:image/png;base64,) !important;
	background-attachment: fixed !important;
	background-repeat: repeat !important;
	color:#000 !important;
}
.special
{
	left: 128px !important;
}
.linkme
{
	right: 24px !important;
}
.quoted-message
{
	background-color: rgba(255, 243, 0, 0.05) !important;
}
.quickpost
{
	background-color: rgba(59, 24, 187, 0.6) !important;
}
.quickpost-canvas
{
	background-color: rgba(168, 168, 168, 0.2) !important;
}
html > body > div > table > tbody > tr > td > table > tbody > tr > th
{
	background-color: rgba(180, 180, 180, 0.7) !important;
	color: #191919 !important;
}
html > body > div > table > tbody > tr > td > table > tbody > tr > td
{
	background-color: rgba(168, 168, 168, 0.5) !important;
}
.message
{
	background-color: rgba(168, 168, 168, 0.65) !important;
}
.userpic
{
	background-color: rgba(123, 123, 123, 0.6) !important;
}
.userbar
{
	background-color: rgba(180, 180, 180, 0.8) !important;
	border-top-left-radius: 20px !important;
	border-top-right-radius: 20px !important;
}
.menubar
{
	background-color: rgba(6, 255, 0, 0.9)!important;
	color: #191919 !important;
}
html > body > div > div.menubar
{
	border-radius: 20px 20px 20px 20px !important;
}
.infobar
{
	background-color: rgba(180, 180, 180, 0.7) !important;
	color: #191919 !important;
}
#u0_4
{
	border-bottom-left-radius: 20px !important;
	border-bottom-right-radius: 20px !important;
}
.message-top
{
	background-color: rgba(123, 123, 123, 0.6) !important;
}
a:link
{
	color:#383838 !important;
}
a:visited
{
	color:#505050 !important;
}
a:hover
{
	color:#ea2bfc !important;
}
a:active
{
	color:#21ff40 !important; /*ea2bfc*/
}

h1
{
	color:#dedede !important;
}

h2
{
	color:#dedede !important;
}
html > body > div > table > tbody > tr > td
{
	background-color: rgba(168, 168, 168, 0.6) !important;
}
html > body > div > table > tbody > tr > th
{
	background-color: rgba(123, 123, 123, 0.8) !important;
}
}
