css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-tab:not([pinned="true"]){
         background-image: -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.05) 95%),
         -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.25) 95%)  !important;
         border-right: 0px solid rgba(0,0,0,.3) !important;
         border-radius: 3px 3px 0px 0px !important;
         box-shadow: inset 0px 0px 0px -5px #404040, inset 0px 1px 1px #000000, 0px 2px 4px #fffafa, 0px 0px 1px #404040!important;
}


.tabbrowser-tab:hover:not([pinned]){
         background-image: -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.05) 95%),
         -moz-linear-gradient(rgba(255,255,255,0.0), rgba(255,255,255,.25) 95%)  !important;
}
