css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* statusbarex for dark themes on Firefox 4 */

#sbex-network-selector,#sbex-logo {display:none !important;}

#sbex-memory {
	width: 90px !important;
	max-width: 90px !important;
}

#sbex-network-value {
text-shadow: none !important;
font-weight: bold !important;
color: #FFFFFF !important;
}

#sbex-cpu-fx-value {color: #FFFFFF !important;}

#sbex:hover {background:none !important;}

hbox.sbex-cpu-item#sbex-cpu-fx {margin-right:-25px !important;}

#sbex-memory-all {
	background: -moz-linear-gradient(#999, #111) !important;
	border: none !important;
}

#sbex-memory-used {
	width: 0;
	border-radius: 4px 0 0 4px;
	background: -moz-linear-gradient(#e63, #500) !important;
}

#sbex-memory-value {
font-weight: bold !important;
}

.sbex-cpu-used {
	background: -moz-linear-gradient(left, #0a0, #0f0) !important;
}
