css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
body{
background: url('http://img850.imageshack.us/img850/4851/dash1.jpg') bottom center #fefefc no-repeat fixed !important;
font-family: verdana !important;
font-size:10px !important;}

ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #000000 !important;}

ol#posts li blockquote blockquote,
body.mceContentBody blockquote blockquote {
border-color: #000000 !important;}
ol#posts li blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote {
border-color: #000000!important;}

ol#posts li blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote {
border-color: #000000!important;}

ol#posts li blockquote blockquote blockquote blockquote blockquote,
body.mceContentBody blockquote blockquote blockquote blockquote blockquote {border-color: #000000 !important;}

ol#posts li.notification.single_notification {border-bottom: 0px solid #000000;}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0) !important;}

.no_posts_found, h1.dashboard_header {color: #666666;}

img#content_top, img#content_bottom {display: none;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;}

h1,h2,h3,h4,h5,h6{color:#b75748 !important;}

a, a:visited{color:#b75748 !important;}

a:hover{color:#813f33 !important;
font-style:italic !important;}

ol#posts li.notification {
background: rgba(196,175,172,0.6) !important;
border-bottom: 1px solid #000000;}

.post_question, #left_column #posts .post .permalink {background: rgba(114,96,94,0.8) !important;}

.post_question_nipple, .arrow{background:transparent!important;}

li.post{
background: rgba(196,175,172,0.6) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border: 0px !important;
border-radius: 20px;
margin: auto;
padding: 20px 20px;}

ol#posts li.post.new_post{
background: rgba(196,175,172,0.6) !important;
border:0px !important;}

#posts .notification.single_notification,
#posts .notification.last_notification {
border-bottom:0px solid #24394E;}

#posts .notification .reply_pane, #posts .post .reply_pane,
#posts ol.notes li.reblog,
#posts ol.notes li.like {
background: rgba(0,0,0,0) !important;
border-bottom:0px;}

.follower {background: rgba(147,126,123,0.5) !important;}

.follower.alt {background: rgba(147,126,123,0.5) !important;}

.follower img.avatar {
background: rgba(115,97,95,0.8) !important;
border:0px solid #C7C7C7;}

#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {color:#000000!important;}

#right_column a#dashboard_switch_blog_menu_current 

#dashboard_switch_blog_arrow_current {background: none;}

#right_column .dashboard_nav_item {
background-color: transparent !important;
background-image:none !important;
border: 0px !important;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs 
#right_column a#dashboard_switch_blog_menu_current:hover {
background: none;}

#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: transparent !important;
background-image:none !important;}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, 
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video{
background-color: transparent !important;
background-image:none !important;}

#nav .nav_item.active {background: rgba(0,0,0,0) !important;}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {background: rgba(0,0,0,0) !important;}

ol#posts li.notification.alt {background-color: rgba(0,0,0,0);}

ol#posts li.notification.first_notification {background-color: rgba(0,0,0,0);}

ol#posts li.notification {
background-color: rgba(0,0,0,0);
border-bottom: 0px solid #000000;}

ol#posts li.notification.last_notification {border-bottom: 0px solid #000000;}

ol#posts li.post .permalink {background: rgba(0,0,0,0);}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0);
border: 0px solid #000000;}

ol#posts li.post .post_avatar{border: 0px !important;}

.dashboard_controls_suggested_blog {
border-top: 0px solid #000000;
border-bottom: 0px solid #000000;}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}

#dashboard_switch_blog_arrow_current {background: none;}

form.dashboard_options_form {
background-color: rgba(0,0,0,0);
border-bottom: 0px solid #000000;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #000000;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;}

form.dashboard_options_form .option_container {border-top: 0px solid #000000;}

.no_posts_found, h1.dashboard_header {color: #000000;}

.answer_container {
background-color: #000000;
border-bottom: 0px solid #000000;}

#nav .nav_item .nav_menu {
background-color: #000000;
border-bottom: 0px solid #000000;}

#nav .nav_item .nav_menu a {
border-top: 0px solid #000000;background-color: transparent !important;
background-image:none !important;}

#auto_pagination_loader {

background: none;

border-top: 0px solid #000000;}

}



@-moz-document url-prefix('http://www.tumblr.com/reblog/'), url-prefix('https://www.tumblr.com/reblog/')   {

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}

}



