div.l-content.l-content.l-content {padding: 40px 0;}

}



@-moz-document url('http://www.tumblr.com/goodies') {

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}

}



@-moz-document url-prefix('http://www.tumblr.com/edit/'), url-prefix('https://www.tumblr.com/edit/')   {

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding: 40px 0;}

}
