@namespace url(http://www.w3.org/1999/xhtml);

@namespace url(http://www.w3.org/1999/xhtml);

/* Youtube COSMIC PANDA */
 @-moz-document
	domain(youtube.com){
	/* title restyle */
		#watch-headline-title {height:30px!important;max-height:30px!important}
		#eow-title { font-size:24px!important;  color:#E4E4E4!important; text-transform:capitalize!important; text-shadow: #000 3px 3px 3px!important;}
		a#watch-headline-show-title			{color:#328cb7!important; text-shadow: #000 3px 3px 6px!important}
		a#watch-headline-show-title:focus	{color:#209bd6!important; background-color: transparent!important; outline:0px!important; -moz-outline-radius: 0px!important;}

	/* buttons */
        #watch-headline-user-info {margin-left:825px!important;}
    


		.yt-uix-expander-collapsed.yt-uix-button.yt-uix-button-default{
		left:-30px!important;
		margin:0px!important;
		padding:0 0px 3px 3px!important;
		border-radius:6px!important;
		position:relative!important;
		height:30px!important;
		width:110px!important;
		font-size:14px!important;
		color: #e4e4e4!important;
		text-shadow: #000 3px 3px 3px!important;
		background-image: -moz-linear-gradient(center top , #555555 0pt, #333333 100%)!important;
		border-color: #1F1F1F!important;
		box-shadow: 0 0 1px #1e1e1e inset!important; }
		

		.yt-subscription-button {
		left:115px!important;
		margin:0px 0px 0px 0px!important;
		padding:0px 0px 2px 6px!important;
		border-radius:6px!important;
		position:relative!important;
		height:30px!important;
		width:30px!important;
		font-size:0px!important;
		color: #e4e4e4!important;
		text-shadow: #000 3px 3px 3px!important;
		background-image: -moz-linear-gradient(center top , #555555 0pt, #333333 100%)!important;
		border-color: #1F1F1F!important;
		box-shadow: 0 0 1px #1e1e1e inset!important; }
		

	/* video resize */
		#watch-player, #watch-video {width:980px!important;  height:582px!important;  margin-left:auto!important;  margin-right:auto!important;}
		#watch-video-container, #watch-headline-container {width:100%!important;background:none!important; background-color:#252525!important}
		#watch-headline-user-info {position:absolute!important; top:66px!important}
		#watch-headline {padding:6px 0 0px 0!important}
		#watch-sidebar {margin-top:0px!important;padding-top:10px!important}

	/* remove unnecessary things */
	    #earth-hour-switches,
		#watch-userbanner,
		.start.yt-uix-button.yt-uix-button-default[href^="/user"],
		.yt-alert.yt-alert-info.yt-rounded,
		#ticker,
		#ytf-main-div,
		#watch-privacy-contain,
		#watch-description-extras,
		#watch-video-extra,
		#pagebottom, [id^="footer"]{display:none!important}
}


/* Google, Leftnav fix, no more unnecessary overflow*/
 @-moz-document

	url-prefix(https://www.google),
	url-prefix(http://www.google),
	url-prefix(https://google),
	url-prefix(http://google),
	url-prefix(www.google),
	url-prefix(google),

	url-prefix(https://www.images.google),
	url-prefix(http://www.images.google),
	url-prefix(https://images.google),
	url-prefix(http://images.google),
	url-prefix(www.images.google),
	url-prefix(images.google) {

		#leftnav		{height:auto!important}
		#leftnav:hover	{overflow: hidden!important}

		.lnsep:first-child,
		#ms,
		#bottomads,
		#taw[style="margin-right:0"]:first-child
			{display:none!important}}





