css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tabmodalprompt {
	/*background-image: -moz-radial-gradient(
		center,
		ellipse farthest-corner,
		rgba(0,0,0,.2),
		rgba(0,0,0,.8)
	) !important;*/
	/*background: rgba(0,0,0,.375) url() !important;*/
	background-image: -moz-repeating-linear-gradient(
		top left -35deg,
		rgba(0,0,0,.1),
		rgba(0,0,0,.1) 3px,
		rgba(255,255,255,.1) 4px,
		rgba(255,255,255,.1) 7px,
		rgba(0,0,0,.1) 8px
	) !important;
	background-image: repeating-linear-gradient(
		-55deg,
		rgba(0,0,0,.1),
		rgba(0,0,0,.1) 3px,
		rgba(255,255,255,.1) 4px,
		rgba(255,255,255,.1) 7px,
		rgba(0,0,0,.1) 8px
	) !important;
}

tabmodalprompt .mainContainer {
	background: transparent !important;
	border-radius: 10px !important;
	box-shadow: 1px 1px 5px rgba(0,0,0,.75), 1px 1px 25px rgba(0,0,0,.5);
	border: 0 !important;
}

tabmodalprompt .topContainer {
	color: #202020 !important;
	background-image: -moz-linear-gradient(
		center bottom,
		#b0b0b0 0%,
		#c0c0c0 10%,
		#e0e0e0 50%,
		#f0f0f0 75%,
		#fff 100%
	) !important;
	background-image: linear-gradient(
		to top,
		#b0b0b0 0%,
		#c0c0c0 10%,
		#e0e0e0 50%,
		#f0f0f0 75%,
		#fff 100%
	) !important;
	border-radius: 9px 9px 0 0;
	text-shadow: 1px 1px rgba(255,255,255,.5);
	box-shadow: inset 0 2px 3px rgba(255,255,255,.75);
}

tabmodalprompt .buttonContainer {
	background-image: -moz-linear-gradient(
		center bottom,
		#101010 0%,
		#202020 45%,
		#303030 55%,
		#505050 100%
	) !important;
	background-image: linear-gradient(
		to top,
		#101010 0%,
		#202020 45%,
		#303030 55%,
		#505050 100%
	) !important;
	box-shadow: inset 0 0 3px rgba(255,255,255,.2);
	border-radius: 0 0 9px 9px;
	border: 0 !important;
}

tabmodalprompt .buttonContainer button {
	-moz-appearance: none !important;
	border: 0 !important;
	padding: 2px 3px 2px 3px;
	margin: 0 5px 0 5px;
	color: #111 !important;
	font-weight: bold;
	font-family: verdana, arial, helvetica, sans-serif !important;
	text-shadow: 1px 1px rgba(255,255,255,.2) !important;
	border-radius: 3px;
	background-image: -moz-radial-gradient(
		center bottom,
		ellipse cover,
		rgba(255,255,255,.375) 0%,
		rgba(255,255,255,0) 50%
	),
	-moz-linear-gradient(
		center bottom,
		#909090 0%,
		#a0a0a0 45%,
		#b8b8b8 55%,
		#d0d0d0 100%
	);
	background-image: radial-gradient(
		ellipse at 50% 100%,
		rgba(255,255,255,.375) 0%,
		rgba(255,255,255,0) 50%
	),
	linear-gradient(
		to top,
		#909090 0%,
		#a0a0a0 45%,
		#b8b8b8 55%,
		#d0d0d0 100%
	);
	cursor: pointer;
	box-shadow: 1px 1px 5px rgba(0,0,0,.5), inset 1px 1px 2px rgba(255,255,255,.25), inset -1px -1px 1px rgba(0,0,0,.2);
}

tabmodalprompt .buttonContainer button:hover {
	color: #333 !important;
	background-image: -moz-radial-gradient(
		center bottom,
		ellipse cover,
		rgba(255,255,255,.5) 0%,
		rgba(255,255,255,0) 50%
	),
	-moz-linear-gradient(
		center bottom,
		#a0a0a0 0%,
		#b0b0b0 45%,
		#c8c8c8 55%,
		#f0f0f0 100%
	);
	background-image: radial-gradient(
		ellipse at 50% 100%,
		rgba(255,255,255,.5) 0%,
		rgba(255,255,255,0) 50%
	),
	linear-gradient(
		to top,
		#a0a0a0 0%,
		#b0b0b0 45%,
		#c8c8c8 55%,
		#f0f0f0 100%
	);
}

tabmodalprompt .buttonContainer button:active {
	color: rgba(0,0,0,.25) !important;
	text-shadow: none !important;
	background-image: -moz-repeating-linear-gradient(
		top left -35deg,
		rgba(128,128,128,0),
		rgba(128,128,128,0) 2px,
		rgba(128,128,128,.2) 3px,
		rgba(128,128,128,.2) 4px,
		rgba(128,128,128,0) 5px
	),
	-moz-radial-gradient(
		center bottom,
		ellipse cover,
		rgba(255,255,255,.375) 0%,
		rgba(255,255,255,0) 50%
	),
	-moz-linear-gradient(
		center bottom,
		#808080 0%,
		#909090 35%,
		#a8a8a8 45%,
		#c0c0c0 100%
	);
	background-image: repeating-linear-gradient(
		-55deg,
		rgba(128,128,128,0),
		rgba(128,128,128,0) 2px,
		rgba(128,128,128,.2) 3px,
		rgba(128,128,128,.2) 4px,
		rgba(128,128,128,0) 5px
	),
	radial-gradient(
		ellipse at 50% 100%,
		rgba(255,255,255,.375) 0%,
		rgba(255,255,255,0) 50%
	),
	linear-gradient(
		to top,
		#808080 0%,
		#909090 35%,
		#a8a8a8 45%,
		#c0c0c0 100%
	);
	text-shadow: 0 0 5px #fff;
	box-shadow: 1px 1px 5px rgba(0,0,0,.5), inset 1px 1px 3px rgba(0,0,0,.625), inset -1px -1px 1px rgba(255,255,255,.375);
	padding: 3px 3px 1px 3px;
}

tabmodalprompt .topContainer textbox {
	-moz-appearance: none !important;
	color: #000 !important;
	background: #fff !important;
	font-family: verdana, arial, helvetica, sans-serif;
	border: 0 !important;
	padding: 3px 2px 2px 3px !important;
	border-radius: 5px;
	box-shadow: inset 1px 1px 3px rgba(0,0,0,.25);
	-moz-transition: box-shadow .1s ease-in;
	transition: box-shadow .1s ease-in;
}

tabmodalprompt .topContainer textbox:hover{
	box-shadow: inset 1px 1px 3px rgba(0,0,0,.25), 0px 0px 5px rgba(255,255,255,.5);
	-moz-transition: box-shadow .1s ease-out;
	transition: box-shadow .1s ease-out;
}
