css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* OVERLAY */
tabmodalprompt {
background: rgba(0, 0, 0, 0.5) !important;
-moz-transition: all .25s ease-in-out !important;
}

/* BOX */
tabmodalprompt .mainContainer {
border-radius: 5px !important;
background: none !important;
-moz-border-image: url("data:image/png;base64,")
15 / 15px repeat !important;
color: #fff !important;
}
/* TOP CONTAINER */
tabmodalprompt .topContainer {
font-size: 11pt !important;
}
/* BOTTOM CONTAINER */
tabmodalprompt .buttonContainer {
background: none !important;
}
/* BUTTONS */
tabmodalprompt .buttonContainer button {
-moz-appearance: none !important;
padding: 3px 0px 1px !important;
border: none !important;
-moz-border-radius: 10px !important;
background: -moz-linear-gradient(top, #fff, #e6e6e6, #fff) !important;
height: 10px !important;
min-height: 0px !important;
-moz-box-shadow: 0px 0px 2px #000 !important;
}
tabmodalprompt .buttonContainer button:hover:active {
background: -moz-linear-gradient(top, #c3ddf1, #7cabdd, #bfe2fb) !important;
}

tabmodalprompt .buttonContainer button[default="true"] {
background: -moz-linear-gradient(top, #d4e7fc, #93c2f4, #dcfdff) !important;
}

/* TEXT BOX */
/* TOP CONTAINER */
tabmodalprompt .topContainer textbox {
height: 24px !important;
font-size: 11pt !important;
}
