@-moz-document url-prefix("http://userstyles.org") , url-prefix("https://userstyles.org") {

/* REMOVE AD */
.ad { display:none !important }
#donate input {
border-radius:3px !important;
height:0 !important;
width:0 !important;
padding-top:23px !important;
padding-left:46px !important;
}

fieldset { border:1px solid #0469A3 !important }

body {
background-color:#fff !important;
color:#666 !important;
font: 12px "Lucida Grande",Helvetica,Arial,Sans-Serif !important;
margin:0 !important }
a:visited { color:#777 !important } /* aaa */
a { color:#0469A3 !important; text-decoration:none !important }

a:hover { color:#4D97C0 !important }

#left-sidebar > #search { border-top:1px solid #eaeaea !important; background-color:#f4f4f4 !important; border-radius:0 !important; padding:15px 10px !important }
#search input { -moz-appearance:none !important; border:1px solid #0469A3 !important }
#search #search-terms { -moz-box-shadow: inset 0 0 5px rgba(0,0,0,0.3); width:225px; background-color:#fff !important; color:#888 !important; padding:7px; border-radius:4px 0 0 4px; border-right:none !important }
#search #search-submit { cursor:pointer;width:70px; background-color:#0469A3 !important; color:#fff !important; padding:6px; border-radius:0 4px 4px 0 }
#search #search-submit:hover { background-color:#5BA9D3 !important; color:#fff !important }
#search #search-terms:focus { background-color:#fff !important; color:#0469A3 !important }
#search select { margin-right:5px !important }
#search br { display:none !important }
#search span { color:#333 !important; padding:6px; text-decoration:none !important }

footer { position:relative !important }
footer a[href="/contact"]:after {
bottom:-25px;
line-height:1em !important;
content: "Userstyles.org - Redesign (2011) Professional by pXqOpAt-G(c)";
color:#aaa;
font-size:x-small;
left:0;
position:absolute;
text-align:center;
text-indent:0;
width:100% }

footer a[href^="/users/"],
article a[href^="http://"],
article a[href^="https://"],
article a[href^="mailto:"] {
display:inline-block !important;
line-height:16px !important;
padding-right:20px !important }

article p a { text-decoration:underline !important }

#style-table td a,
#discussions li a,
article #screenshots a,
article h2 a { padding:0 !important; line-height:inherit !important; background-image:none !important }

article h2 a { color:#4D97C0 !important }
article h2 a:hover { color:#fff !important }

footer#related,
nav, #main-header, section#install {
background-color:transparent !important;
border-radius:0 !important }

#left-sidebar, #content-wrapper { float:none !important }
#main-header h1, footer p, #left-sidebar, #left-sidebar nav ul,
#content-wrapper {  margin:0 auto !important; width:860px !important }

#main-header {
background-color:#0469a3 !important;
background: -moz-linear-gradient(top, #0469a3 0%, #003449 100%) !important;
margin:0 0 45px 0 !important;
padding:0.5em 0 0.5em 0 !important }

#main-header a:visited, #main-header a:hover,
#main-header a { color:#fff !important; font-size:24px !important; font-weight:600!important; text-transform:capitalize !important }
#main-header a:hover { color:#eef !important }
#main-header > h1 {
background-repeat:no-repeat !important;
color:rgba(255,255,255,0.9) !important; font-size:16px !important;
overflow:hidden !important;
padding-left:35px !important; width:835px !important }

footer {
background-color:#0469A3 !important;
border-radius:0 0 10px 10px !important;
font-size:14px !important;
margin:0 auto 40px !important;
padding:0 !important;
width:860px !important;
}

footer p { line-height:60px !important; text-indent:15px }
footer p a:visited,
footer p a { color:#fff !important }

footer p a { padding-left:22px !important }
footer p a:hover { text-decoration:underline !important }

#style-author {
background-color:transparent !important;
border-bottom:1px solid #eee !important;
border-radius:0 !important;
margin-bottom:1em !important;
position:relative !important }

#style-author div:first-child {
background-color:#0469A3 !important;
border:1px solid #074B70 !important;
padding:6px !important }

#style-author div:first-child a,
#style-author div:first-child { color:#fff !important }

#style-author div:first-child a:hover { text-decoration:underline !important }

/* NAVIGATION */
#left-sidebar nav {
top:50px; right:0; height:39px !important;
position:absolute !important;
width:100% }

#left-sidebar > * { padding:0 !important; margin:0 !important }
#left-sidebar footer ul { margin:0.5em 0 !important }

nav ul { border-bottom:1px solid #eee !important; padding:0 0 9px !important; position:relative !important; width:990px !important }

nav ul li { border-left:1px solid #eee !important; display:inline-block !important; font-weight:bold !important; margin:0 !important; width:168.54px !important } /* 143px */
nav ul li:first-child { border-left:none !important }
nav ul li,
nav ul li a,
nav ul li a:visited { color:#aaa !important; font-size:12px !important; text-align:center !important }
nav ul li a { display:block !important; font-weight:400 !important; padding:0 !important }
nav ul li a:hover { color:#888 !important }
nav ul li { color:red !important }

nav ul li input {
-moz-appearance:none !important;
border:1px solid #eee !important;
}

nav ul li #search-submit {
border-left:none !important;
color:#fff !important;
cursor:pointer !important;
text-indent:-9999px !important;
width:17px !important }

nav #search-terms {
border-right:none !important;
color:#aaa !important;
padding:1px 0 1px 3px !important;
width:150px !important;
}

nav #search-terms:focus { color:#666 !important }



#left-sidebar footer h2 { margin:0 !important }

article { margin:1em 0 !important; padding:0 !important }

pre {
background-color:#135 !important;
border-radius:5px;
color:rgba(255,255,255,0.8) !important;
border:1px solid #eee !important;
margin:0.5em 0 0 !important }

#style-author #control-panel { padding:6px 0 4px 0 !important }

#control-panel a,
.delete-link, .edit-link { text-indent:-9999px !important }
.delete-link, .edit-link { width:22px !important }

#control-panel a {
border:1px solid #ddd !important;
padding:5px 5px 6px 30px !important }

#control-panel a:nth-child(2),
#control-panel a:nth-child(1) {
background-color:#f4f4f4 !important;
display:inline-block;
margin-bottom:0.2em !important }

#control-panel a:hover { background-color:#7BC0E5 !important; border-color:#4D97C0 !important }

#obsoletion-message { background-color:#A00404 !important; border-radius:0 !important; padding:5px !important; position:relative; z-index:2; }
#obsoletion-message h2 { background-color:transparent !important; border-bottom-color:rgba(255,255,255,0.3) !important;  color:#fff !important; }
#obsoletion-message p,
#obsoletion-message p a,
#obsoletion-message h2 { color:#fff !important }

#subcategory-list {
-moz-column-count:3 !important;
-moz-column-rule: thin dotted #eee;
-moz-column-gap: 10px;
-webkit-column-count:3 !important;
-webkit-column-rule: thin dotted #eee;
-webkit-column-gap: 10px;
padding:0 !important }

#subcategory-list li { border-bottom:1px solid #eee !important; line-height:22px !important; list-style:none !important }
#subcategory-list li:hover { background-color:#4D97C0 !important }
#subcategory-list li:hover a { color:#fff !important }
#subcategory-list li a { display:block !important; padding:4px !important }

#style-table { width:100% }
#style-table th { background-color:#0469A3 !important; color:#fff !important }
#style-table th, #style-table td { border-color:#eee !important }

#style-table tr:nth-child(2n+1) { background-color:#f4f4f4 !important }
#style-table tr:nth-child(2n+2) { background-color:#fafafa !important }

#style-table .obsolete:not(:hover) { opacity:1 !important }

#style-table .obsolete a { color:#a77 !important }
#style-table .obsolete a:first-child:after { content: " (Deleted)"; color:#e00 !important; font-style:italic !important }

}

@-moz-document url("http://userstyles.org/") , url("https://userstyles.org/") {

#front-page-best { margin-right:10px !important }
#front-page-best, #front-page-newest { border-top:1px solid #eaeaea !important; max-width:inherit !important; width:423.3px !important }
#front-page-best,
#front-page-newest,
#front-page-best h2,
#front-page-newest h2 { border-radius:5px 5px 0 0 !important }

#front-page-best h2,
#front-page-newest h2 { padding-left:10px !important }

article div ul { padding:0 !important }
article div ul li { border-bottom:1px solid #eee !important; height:25px !important; line-height:25px !important; list-style:none !important; overflow:hidden; padding:2px 3px 2px 18px !important }

article div ul li:last-child a:visited,
article div ul li:last-child a { color:#0469A3 !important; text-transform:capitalize!important }
article div ul li:last-child:before { content:none !important }
article div ul li a { text-transform:lowercase !important; display:block !important; padding:1px 3px !important }
article div ul li a:after { content: " (Visited)"; color:transparent !important; font-style:italic !important }
article div ul li:last-child a:after { content:none !important }
article div ul li a:visited { color:#999 !important } /* bbb */
article div ul li a:visited:after { color:#ddd !important }

article div ul li:hover a:after { color:#4D97C0 !important }
